methods.method:
function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false; 
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				if(oCurObj.jxParams.children("hide_on_click").text()=="yes")
				{
					jCurObjDiv.find(".cl-btn-close-object").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				oCurObj.data =
				{
					bCreated: true,
					bTypeWriter: (oCurObj.jxParams.children("typewriter").text()=="yes")
				};
				if(oCurObj.data.bTypeWriter)
				{
					var sSpeed = oCurObj.jxParams.children("typewriter_speed").text();
					var iSpeed = (sSpeed=="") ? 10 : parseInt(sSpeed, 10);
					if(isNaN(iSpeed)) iSpeed = 10;
					oCurObj.data.iDelay = Math.round(1000/iSpeed);
				}
				var sHTML;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					sHTML = CL.Substitute.All({ sString: oCurObj.oStates[sState].sText });
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box-cell").html( sHTML );
				}
				if(CL.bTouch)
				{
					oCurObj.data.jTextBox.on("touchmove", function(e) { e.stopPropagation() }).addClass("cl-touch-scroll");
				}
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".cl-container:first").hide();
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null) return false; 
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				jCurObjDiv.find(".cl-container:first").show();
				if(oCurObj.data.bTypeWriter)
				{
					oCurObj.CallMethod({ sMethod: "TypeWriter", oMethodArgs: { oElem: jCurObjDiv.find("div[data-state='default'] .cl-txt-box-cell"), iDelay: oCurObj.data.iDelay, iItem: 0 } });
				}
				return true;
			}

function TypeWriter(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = $(argobj.oElem); 
				if(jElem.attr("data-wrapped")=="yes")
				{
					jElem.find(".cl-char").each(function () { $(this).contents().unwrap() });
				}
				jElem.attr({ "data-wrapped": "yes" }).html( CL.Common.WrapCharacters({ sHTML: jElem.html() }) );
				oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: jElem, iDelay: oCurObj.data.iDelay, iItem: 0 } });
				return true;
			}

function NextChar(argobj)
			{
				if(argobj==null) return false;
				if(argobj.pid==null || argobj.oElem==null || argobj.iDelay==null || argobj.iItem==null)
				{
					return false;
				}
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null)
				{
					return false;
				}
				var jElem = $(argobj.oElem);
				var jItem = jElem.find(".cl-char[data-i='" + argobj.iItem + "']");
				if(jItem.length==0)
				{
					oCurObj.FireEvent({ sName: "onTypeWriterEnd" });
					return false;
				}
				jItem.contents().unwrap();
				oCurObj.iTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: argobj.oElem, iDelay: argobj.iDelay, iItem: (argobj.iItem+1) } }); }, argobj.iDelay);
				return true;
			}

function UIEvent(argobj)
			{
				if(argobj.evt==null) return false;
				var jElem = $(argobj.elem);
				switch(argobj.evt.type)
				{
					case "click":
					{
						CLO[argobj.pid].CallMethod({ sMethod: "Close" });
						break;
					}
				}
				return true;
			}

function Close(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				oCurObj.Hide();
				oCurObj.FireEvent({ sName: "onCloseObject" });
				return true;
			}

function RefreshText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onRefresh" });
				return true;
			}

function ResetText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onReset" });
				return true;
			}

function AddText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				var sText;
				for(var sState in oCurObj.oStates)
				{
					sText = CL.Common.AddText({ sHTML: oCurObj.oStates[sState].sText, sText: argobj.value, sWhere: argobj.where, sHow: argobj.how });
					oCurObj.oStates[sState].sText = sText;
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onAddText" });
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return "";
				if(CLO[argobj.pid]==null) return "";
				switch(argobj.property.toLowerCase())
				{
					case "state":
					{
						return oCurObj.sCurrentState;
					}
				}
				return "";
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" }, { sId: argobj.pid, sEvtName: "EVENT_OBJECT_HIDE" } ] });
				oCurObj.data = { bActive: false, sHLType: oCurObj.jxParams.find("highlight_type").text(), nDuration: +oCurObj.jxParams.find("timer").text() };
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null || oCurObj.div==null) return false;
				if(oCurObj.data.sHLType=="simple") return false;
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(oCurObj.data.bActive) return true;
						oCurObj.CallMethod({ sMethod: "On", oMethodArgs: { bDeferred: true } });
						break;
					}
					case "EVENT_OBJECT_HIDE":
					{
						if(!oCurObj.data.bActive) return true;
						oCurObj.data.bActive = false;
						clearTimeout(oCurObj.iHLTimer);
						break;
					}
				}
				return true;
			}

function On(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null || oCurObj.div==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				if(jCurObjDiv.is(":visible"))
				{
					switch(oCurObj.data.sHLType)
					{
						case "blink":
						{
							jCurObjDiv.find(".cl-notifier").show({ effect: "fade", duration: 100 });
							break;
						}
						case "pulse":
						{
							var iX = Math.floor(0.03*oCurObj.current.w);
							var iY = Math.floor(0.03*oCurObj.current.h);
							jCurObjDiv.find(".cl-notifier").animate({ "left": -iX + "px", "top": -iY + "px", "zoom": 1.06 }, 100);
							break;
						}
					}
					oCurObj.iHLTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Off" }); }, +oCurObj.data.nDuration);
				}
				return true;
			}

function Off(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null || oCurObj.div==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				if(jCurObjDiv.is(":visible"))
				{
					switch(oCurObj.data.sHLType)
					{
						case "blink":
						{
							jCurObjDiv.find(".cl-notifier").hide({ effect: "fade", duration: 100 });
							break;
						}
						case "pulse":
						{
							jCurObjDiv.find(".cl-notifier").css({ "zoom": 1.0, "left": "0", "top": "0" });
							break;
						}
					}
					oCurObj.iHLTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "On" }); } , +oCurObj.data.nDuration);
				}
				else
				{
					clearTimeout(oCurObj.iHLTimer);
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				var sRole = oCurObj.jxParams.children("role").text();
				var sTarget = (sRole.indexOf("slide")==-1) ? "frame" : "slide";
				var sDir = "";
				var sId;
				if(sRole=="closemodule")
				{
					oCurObj.data =
					{
						sRole: "close",
						sState: "enabled",
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
				}
				else
				{
					var bSwipe = false;
					if(sRole.indexOf("next")!=-1)
					{
						sDir = "next";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("prev")!=-1)
					{
						sDir = "prev";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("first")!=-1)
					{
						sDir = "first";
					}
					else if(sRole.indexOf("last")!=-1)
					{
						sDir = "last";
					}
					else if(sRole=="thisslide")
					{
						sDir = "this";
					}
					else
					{
						sId = oCurObj.jxParams.children((sRole=="selectedslide") ? "selectedslide" : "selectedframe").text();
					}
					oCurObj.data =
					{
						sRole: sRole,
						sTarget: sTarget,
						sDir: sDir,
						sId: sId,
						sFrameId: "",
						sSlideId: "",
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
						bNaviLockedByAction: false,
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					jCurObjDiv.addClass("cl-nav-action-" + oCurObj.data.sRole);
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(oCurObj.data.sRole=="close")
					{
						oCurObj.data.bDisplayConfirm = (oCurObj.jxParams.children("display_closemodule_msg").text()=="yes");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_FRAME_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_FRAME_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				}
				if(CL.bTouch)
				{
					if(bSwipe)
					{
						if(sDir=="next")
						{
							$(CL.oBoard).on("swipeleft", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}
						else if(sDir=="prev")
						{
							$(CL.oBoard).on("swiperight", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}

					}
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				else
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sTarget=="slide")
				{
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets==oCurObj.data.sDir || (argobj.sTargets=="restart" && oCurObj.data.sDir=="this") || argobj.sTargets=="all" || (argobj.sTargets=="buttons" && (oCurObj.data.sDir=="next" || oCurObj.data.sDir=="prev")) || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "update":
										{
											oCurObj.CallMethod({ sMethod: "Update" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
				}
				else
				{
					switch(argobj.evt)
					{
						case "EVENT_FRAME_OPENED":
						{
							if(oCurObj.data.sFrameId!=CLZ.sCurrentFrameId)
							{
								oCurObj.data.sFrameId = CLZ.sCurrentFrameId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_FRAME_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="all" || argobj.sTargets=="buttons" || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				if(argobj.evt==null) return false;
				if(oCurObj.data.sState=="disabled" || $(".cl-modal-mask").is(":visible")) return false;
				switch(argobj.evt.type)
				{
					case "swiperight":
					case "swipeleft":
					{
						oCurObj.CallMethod({ sMethod: "Navigate" });
						break;
					}
				}
				return true;
			}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jBtn = $(CLO[argobj.pid].div).find(".cl-btn");
				if(jBtn.hasClass("cl-disabled") && argobj.sState!="enabled") return false;
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "disabled":
					{
						jBtn.removeClass("cl-idle cl-over").addClass("cl-disabled");
						CLO[argobj.pid].data.sState = "disabled";
						break;
					}
					case "enabled":
					{
						jBtn.removeClass("cl-disabled").addClass("cl-idle");
						CLO[argobj.pid].data.sState = "enabled";
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
				return true;
			}

function EnableAccess(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sRole=="close")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "enabled" } });
				}
				else
				{
					var oParams = { sTarget: CLO[argobj.pid].data.sTarget };
					if(CLO[argobj.pid].data.sDir=="")
					{
						oParams.sTargetId = CLO[argobj.pid].data.sId;
					}
					else
					{
						oParams.sDir = CLO[argobj.pid].data.sDir;
					}
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: ( (argobj.override==true || CL.Navigation.Allowed(oParams) || (CL.bAlternativePath && CL.Navigation.Exists(oParams) )) ? "enabled" : "disabled") } });
				}
				return true;
			}

function Navigate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var oParams = { sTargetType: oCurObj.data.sTarget, bUserAction: true };
				if(oCurObj.data.sDir=="")
				{
					oParams.sTargetId = oCurObj.data.sId;
				}
				else
				{
					oParams.sTargetDir = oCurObj.data.sDir;
				}
				var sCLXNavi = "";
				CLS[CLZ.sCurrentSlideId].jxObjects.each(function()
				{
					if(this.getAttribute("type")=="clx_navigation")
					{
						sCLXNavi = this.getAttribute("id");
					}
				});
				if(sCLXNavi!="")
				{
					if((oCurObj.data.sDir=="next" && CLO[sCLXNavi].data.bCustomNext) || (oCurObj.data.sDir=="prev" && CLO[sCLXNavi].data.bCustomPrev))
					{
						return CLO[sCLXNavi].CallMethod({ sMethod: "ProcessNavigation", oMethodArgs: { sDir: oCurObj.data.sDir } });
					}
				}
				if(argobj.override==true || CL.Navigation.Allowed(oParams))
				{
					oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
					CL.Navigation.GoTo(oParams);
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function GetProperty(argobj)
			{
				return CLO[argobj.pid].GetProperty(argobj);
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="disabled") return false;
				if(oCurObj.data.sRole=="close")
				{
					if(oCurObj.data.bDisplayConfirm)
					{
					if(confirm(CLO[argobj.pid].jxParams.children("closemodule_msg").text()))
						{
							window.close();
						}
					}
					else
					{
						window.close();
					}
				}
				else
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Navigate" });
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				oCurObj.data = { bActive: false, iAttempts: +oCurObj.jxParams.find("attempts").text(), sControl: oCurObj.jxParams.find("control_type").text(), sActivate: oCurObj.jxParams.find("activation").text(), oZone: {} };
				switch(oCurObj.data.sControl)
				{
					case "slide":
					{
						oCurObj.data.oZone.nX = 0;
						oCurObj.data.oZone.nY = 0;
						oCurObj.data.oZone.nW = $(CL.oBoard).width();
						oCurObj.data.oZone.nH = $(CL.oBoard).height();
						break;
					}
					case "object":
					{
						var sTargetId = oCurObj.jxParams.find("object_to_control").text();
						if(CLO[sTargetId]==null) return false;
						oCurObj.data.oZone.nX = CLO[sTargetId].current.x;
						oCurObj.data.oZone.nY = CLO[sTargetId].current.y;
						oCurObj.data.oZone.nW = CLO[sTargetId].current.w;
						oCurObj.data.oZone.nH = CLO[sTargetId].current.h;
						break;
					}
					case "zone":
					{
						oCurObj.data.oZone.nX = +oCurObj.jxParams.find("pX").text();
						oCurObj.data.oZone.nY = +oCurObj.jxParams.find("pY").text();
						oCurObj.data.oZone.nW = +oCurObj.jxParams.find("pW").text();
						oCurObj.data.oZone.nH = +oCurObj.jxParams.find("pH").text();
						break;
					}
				}
				oCurObj.data.jZone = $(document.createElement("div")).addClass("cl-clickarea").attr({ "id": argobj.pid + "_CLICKAREA" }).css({ "position": "absolute", "left": oCurObj.data.oZone.nX + "px", "top": oCurObj.data.oZone.nY + "px", "width": oCurObj.data.oZone.nW + "px", "height": oCurObj.data.oZone.nH + "px", cursor: "default", "z-index": 4998, "cursor": oCurObj.jxParams.find("cursor").text() }).appendTo(CL.oBoard).hide();
				oCurObj.data.jZone.on("click", function (e) { oCurObj.CallMethod({ sMethod: "HandleClick", oMethodArgs: { evt: e, elem: this } }) });
				if(oCurObj.data.sActivate=="display")
				{
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false; 
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						oCurObj.CallMethod({ sMethod: "Activate" });
						break;
					}
				}
				return true;
			}

function Activate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				oCurObj.data.iAttemptsLeft = oCurObj.data.iAttempts;
				oCurObj.data.bActive = true;
				oCurObj.data.jZone.show();
				oCurObj.FireEvent({ sName: "onStartControl" });
				return true;
			}

function Dectivate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				oCurObj.data.bActive = false;
				oCurObj.FireEvent({ sName: "onStopControl" });
				return true;
			}

function Destroy(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.jZone.length!=0)
				{
					oCurObj.data.jZone.remove();
				}
				oCurObj.data.bActive = false;
				return true;
			}

function HandleClick(argobj)
			{
				if(argobj.evt==null || argobj.elem==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(!oCurObj.data.bActive) return false; 
				if(argobj.evt.pageX)
				{
					iEventX = argobj.evt.pageX;
					iEventY = argobj.evt.pageY;
				}
				else if(argobj.evt.offsetX)
				{
					iEventX = argobj.evt.offsetX;
					iEventY = argobj.evt.offsetY;
				}
				else
				{
					return false;
				}
				iEventX /= CLZ.nZoom;
				iEventY /= CLZ.nZoom;
				var oBoardOffset = $(CL.oBoard).offset();
				var iOffsetX = oBoardOffset.left/CLZ.nZoom;
				var iOffsetY = oBoardOffset.top/CLZ.nZoom;
				var iPX1 = oCurObj.data.oZone.nX + iOffsetX;
				var iPY1 = oCurObj.data.oZone.nY + iOffsetY;
				var iPX2 = oCurObj.data.oZone.nX + oCurObj.data.oZone.nW + iOffsetX;
				var iPY2 = oCurObj.data.oZone.nY + oCurObj.data.oZone.nH + iOffsetY;
				// out of control?
				if(iEventX<iPX1 || iEventX>iPX2 || iEventY<iPY1 || iEventY>iPY2)
				{
					oCurObj.FireEvent({ sName: "onClickOutOfControlArea" });
					return false;
				}
				// we're in
				oCurObj.FireEvent({ sName: "onClickInControlArea" });

				var iZX1 = oCurObj.current.x + iOffsetX;
				var iZY1 = oCurObj.current.y + iOffsetY;
				var iZX2 = oCurObj.current.x + oCurObj.current.w + iOffsetX;
				var iZY2 = oCurObj.current.y + oCurObj.current.h + iOffsetY;

				if(iEventX>=iZX1 && iEventX<=iZX2 && iEventY>=iZY1 && iEventY<=iZY2)
				{
					oCurObj.FireEvent({ sName: "onClickCorrect" });
					oCurObj.CallMethod({ sMethod: "ProcessCorrect" });
				}
				else
				{
					oCurObj.FireEvent({ sName: "onClickIncorrect" });
					oCurObj.CallMethod({ sMethod: "ProcessIncorrect" });
				}
				return true;
			}

function ProcessCorrect(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				oCurObj.data.bActive = false;
				oCurObj.jxParams.find("correct_display > item").each(function ()
				{
					var sTargetId = $(this).children("objid").text();
					if(CLO[sTargetId]!=null)
					{
						CLO[sTargetId].Show();
					}
				});
				oCurObj.jxParams.find("correct_hide > item").each(function ()
				{
					var sTargetId = $(this).children("objid").text();
					if(CLO[sTargetId]!=null)
					{
						CLO[sTargetId].Hide();
					}
				});
				oCurObj.CallMethod({ sMethod: "Destroy" });
				oCurObj.FireEvent({ sName: "onStopControl" });
				return true;
			}

function ProcessIncorrect(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(!oCurObj.data.bActive) return false;
				oCurObj.data.iAttemptsLeft = oCurObj.data.iAttemptsLeft - 1;
				var iAttempt = oCurObj.data.iAttempts - oCurObj.data.iAttemptsLeft;
				var jxAttemptActions = oCurObj.jxParams.find("attempts_actions > item"); 
				if(oCurObj.data.iAttemptsLeft>0)
				{
					var jxActions;
					for(var i=0; i<jxAttemptActions.length; i++)
					{
						if(+$(jxAttemptActions[i]).children("attemptnumber").text()==iAttempt)
						{
							jxActions = $(jxAttemptActions[i]);
							break;
						}
					}
					if(jxActions==null) return false;
					jxActions.find("attempt_display > item").each(function ()
					{
						var sTargetId = $(this).children("objid").text();
						if(CLO[sTargetId]!=null)
						{
							CLO[sTargetId].Show();
						}
					});
					jxActions.find("attempt_hide > item").each(function ()
					{
						var sTargetId = $(this).children("objid").text();
						if(CLO[sTargetId]!=null)
						{
							CLO[sTargetId].Hide();
						}
					});
				}
				else
				{
					var sLastAttemptType = oCurObj.jxParams.find("lastattempt").text();
					if(sLastAttemptType.indexOf("list")!=-1)
					{
						var jxActions;
						for(var i=0; i<jxAttemptActions.length; i++)
						{
							if(+$(jxAttemptActions[i]).children("attemptnumber").text()==iAttempt)
							{
								jxActions = $(jxAttemptActions[i]);
								break;
							}
						}
						if(jxActions==null) return false;
						jxActions.find("attempt_display > item").each(function ()
						{
							var sTargetId = $(this).children("objid").text();
							if(CLO[sTargetId]!=null)
							{
								CLO[sTargetId].Show();
							}
						});
						jxActions.find("attempt_hide > item").each(function ()
						{
							var sTargetId = $(this).children("objid").text();
							if(CLO[sTargetId]!=null)
							{
								CLO[sTargetId].Hide();
							}
						});
					}
					if(sLastAttemptType.indexOf("select")!=-1)
					{
						oCurObj.CallMethod({ sMethod: "Highlight" });
					}
					oCurObj.CallMethod({ sMethod: "Destroy" });
					oCurObj.FireEvent({ sName: "onStopControl" });
				}
				return true;
			}

function Highlight(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				oCurObj.HLOff = function ()
				{
					$(oCurObj.div).find(".cl-highlight").hide({ effect: "fade", duration: 100 });
				};
				$(oCurObj.div).find(".cl-highlight").show({ effect: "fade", duration: 100 });
				oCurObj.iHLTimer = setTimeout(function () { oCurObj.HLOff(); }, +oCurObj.jxParams.find("timer_incorrect").text());
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false;
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				var jSBtn = jCurObjDiv.find(".cl-btn-submit");
				jSBtn.parent().height(jSBtn.outerHeight());

				oCurObj.data =
				{
					aQuestions: [],
					bAllAnswered: false,
					bAllowQTimer: (oCurObj.jxParams.children("test_q_timer_switch").text()=="yes"),
					bAllowFeedback: (oCurObj.jxParams.children("q_fb_switch").text()=="yes"),
					bCredit: (oCurObj.jxParams.children("test_scored").text()=="yes"),
					bCombineBtn: (oCurObj.jxParams.children("combine_btn_next").text()=="yes"),
					bDisplayGoal: (oCurObj.jxParams.children("display_goal").text()=="yes"),
					bQuestionTimer: (oCurObj.jxParams.children("test_q_timer_switch").text()=="yes"),
					bRandom: (oCurObj.jxParams.children("randomize").text()=="yes"),
					bShowCorrect: (oCurObj.jxParams.children("q_showright").text()=="yes"),
					bTestTimer: (oCurObj.jxParams.children("test_timer_switch").text()=="yes"),
					jxQuestions: oCurObj.jxParams.find("q_questions > item"),
					sDisplayAs: oCurObj.jxParams.find("display_type").text(),
					sMode: "init",
					sSoundSuccess: oCurObj.jxParams.children("sound_success").text(),
					sSoundFailure: oCurObj.jxParams.children("sound_failure").text(),
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				if(oCurObj.data.jxQuestions.length==0)
				{
					return false;
				}
				var jAudioContainer = jCurObjDiv.find(".cl-audio-files-list");
				if(oCurObj.data.sSoundClick!="none")
				{
					oCurObj.data.jAudioClick = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO1", sType: oCurObj.data.sSoundClick, sFile: oCurObj.jxParams.children("sound_click_file_custom").text() });
				}
				if(oCurObj.data.sSoundOver!="none")
				{
					oCurObj.data.jAudioOver = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO2", sType: oCurObj.data.sSoundOver, sFile: oCurObj.jxParams.children("sound_over_file_custom").text() });
				}
				if(oCurObj.data.sSoundSuccess!="none")
				{
					oCurObj.data.jAudioSuccess = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO3", sType: oCurObj.data.sSoundSuccess, sFile: oCurObj.jxParams.children("sound_success_file_custom").text() });
				}
				if(oCurObj.data.sSoundFailure!="none")
				{
					oCurObj.data.jAudioFailure = CL.Sound.Append({ oContainer: jAudioContainer, sId: argobj.pid + "_AUDIO4", sType: oCurObj.data.sSoundFailure, sFile: oCurObj.jxParams.children("sound_failure_file_custom").text() });
				}
// fill runtime-only params fo all q
				var jxQuestion;
				var iIdx;
				for(var i=0; i<oCurObj.data.jxQuestions.length; i++)
				{
					jxQuestion = $(oCurObj.data.jxQuestions[i]);
					oCurObj.data.aQuestions[i] =
					{
						bDisplayText: (jxQuestion.children("display_question_text").text()=="yes"),
						sType: jxQuestion.children("question_type").text()
					}
					if(oCurObj.data.bQuestionTimer)
					{
						if(jxQuestion.children("q_timer_switch").text()=="yes")
						{
							var iTimer = +jxQuestion.children("q_timer").text();
							if(isNaN(iTimer))
							{
								oCurObj.data.aQuestions[i].bTimer = false;
							}
							else
							{
								oCurObj.data.aQuestions[i].bTimer = true;
								oCurObj.data.aQuestions[i].iTimer = iTimer;
							}
						}
					}
					if(oCurObj.data.bCredit)
					{
						oCurObj.data.aQuestions[i].aObjectives = [];
						jxQuestion.find("q_scoreboards > item").each(function ()
						{
							oCurObj.data.aQuestions[i].aObjectives.push({ sId: $(this).children("q_board").text(), nWeight: +$(this).children("q_weight_byobj").text() });
						});
					}
					switch(oCurObj.data.aQuestions[i].sType)
					{
						case "true-false":
						{
							oCurObj.data.aQuestions[i].sScoringType = jxQuestion.children("q_scoring_type_tf").text();
							oCurObj.data.aQuestions[i].bShuffle = (jxQuestion.children("q_elements_order").text()=="random");
							break;
						}
						case "choice":
						{
							oCurObj.data.aQuestions[i].sScoringType =jxQuestion.children("q_scoring_type_choice").text();
							oCurObj.data.aQuestions[i].jxVars = jxQuestion.find("q_variants_choice > item");
							oCurObj.data.aQuestions[i].bShuffle = (jxQuestion.children("q_shuffle_choice").text()=="yes");
							break;
						}
						case "select":
						{
							oCurObj.data.aQuestions[i].sScoringType = jxQuestion.children("q_scoring_type_choice").text();
							oCurObj.data.aQuestions[i].jxVars = jxQuestion.find("q_variants_select > item");
							oCurObj.data.aQuestions[i].bShuffle = (jxQuestion.children("q_shuffle_select").text()=="yes");
							break;
						}
						case "order":
						{
							oCurObj.data.aQuestions[i].sScoringType = jxQuestion.children("q_scoring_type_choice").text();
							oCurObj.data.aQuestions[i].jxVars = jxQuestion.find("q_variants_order > item");
							oCurObj.data.aQuestions[i].bShuffle = true;
							break;
						}
						case "numeric":
						{
							oCurObj.data.aQuestions[i].sScoringType = jxQuestion.children("q_scoring_type_choice").text();
							oCurObj.data.aQuestions[i].jxVars = jxQuestion.find("q_variants_numeric > item");
							oCurObj.data.aQuestions[i].sOp = jxQuestion.children("q_var_logic_numeric").text();
							break;
						}
						case "fib":
						{
							oCurObj.data.aQuestions[i].sScoringType = jxQuestion.children("q_scoring_type_choice").text();
							oCurObj.data.aQuestions[i].jxVars = jxQuestion.find("q_variants_fib > item");
							oCurObj.data.aQuestions[i].sOp = jxQuestion.children("q_var_logic_fib").text();
							break;
						}
						case "oto":
						{
							oCurObj.data.aQuestions[i].sScoringType = jxQuestion.children("q_scoring_type_choice").text();
							oCurObj.data.aQuestions[i].jxVars = jxQuestion.find("q_variants_oto > item");
							break;
						}
					}
				}
// detect mode
				if(CLZ.oStore[argobj.pid]!=null)
				{
					switch(CLZ.oStore[argobj.pid].u)
					{
						case "c":
						{
							switch(oCurObj.jxParams.children("repeat_test").text())
							{
								case "no":
								{
									oCurObj.data.sMode = "browse";
									alert(oCurObj.jxParams.children("t_browse_alert").text());
									break;
								}
								case "yes":
								{
									if(confirm(oCurObj.jxParams.children("t_secondrun_confirm").text()))
									{
										CLZ.oStore[argobj.pid] = {};
										CLZ.oStore[argobj.pid] = null;
										if(oCurObj.data.bCredit)
										{
											for(var i=0; i<oCurObj.data.aQuestions.length; i++)
											{
												for(var j=0; j<oCurObj.data.aQuestions[i].aObjectives.length; j++)
												{
													CL.SCO.RemoveScoreFromObjective({ sId: oCurObj.data.aQuestions[i].aObjectives[j].sId, sSrcId: argobj.pid + "_" + i });
												}
											}
										}
									}
									else
									{
										oCurObj.data.sMode = "browse";
									}
									break;
								}
							}
							break;
						}
						case "i":
						{
							alert(oCurObj.jxParams.children("t_continue_alert").text());
							oCurObj.data.sMode = "continue";
							break;
						}
						default:
						{
							CLZ.oStore[argobj.pid] = {};
							CLZ.oStore[argobj.pid] = null;
							if(oCurObj.data.bCredit)
							{
								for(var i=0; i<oCurObj.data.aQuestions.length; i++)
								{
									for(var j=0; j<oCurObj.data.aQuestions[i].aObjectives.length; j++)
									{
										CL.SCO.RemoveScoreFromObjective({ sId: oCurObj.data.aQuestions[i].aObjectives[j].sId, sSrcId: argobj.pid + "_" + i });
									}
								}
							}
							break;
						}
					}
				}
// create storage
				if(oCurObj.data.sMode=="init")
				{
					CLZ.oStore[argobj.pid] =
					{
						P: [],
						Q: [],
						R: [],
						s: (new Date()).valueOf(),
						y: "o",
						u: "i"
					};
					if(oCurObj.data.bTestTimer)
					{
						var iTimerM = +oCurObj.jxParams.children("test_timer").text();
						var iTimerS = +oCurObj.jxParams.children("test_timer_s").text();
						if(isNaN(iTimerM) && isNaN(iTimerS))
						{
							oCurObj.data.bTimer = false;
						}
						else
						{
							var iTimer = 0;
							if(!isNaN(iTimerM))
							{
								iTimer = 60*iTimerM;
							}
							if(!isNaN(iTimerS))
							{
										iTimer += iTimerS;
							}
							CLZ.oStore[argobj.pid].t = iTimer;
							CLZ.oStore[argobj.pid].e = 0;
						}
					}
					if(oCurObj.data.bRandom)
					{
						var iToPlay = +oCurObj.jxParams.children("random_quantity").text();
						if(iToPlay > oCurObj.data.jxQuestions.length)
						{
							iToPlay = oCurObj.data.jxQuestions.length;
						}
						var aShuffled = [];
						for(var i=0; i<iToPlay; i++) aShuffled[i] = CLTOOLS.Dice({ iMax: oCurObj.data.jxQuestions.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 });
						for(var i=0; i<iToPlay; i++) CLZ.oStore[argobj.pid].P.push(aShuffled[i]);
					}
					else
					{
						for(var i=0; i<oCurObj.data.jxQuestions.length; i++) CLZ.oStore[argobj.pid].P.push(i);
					}
					var oQuestion;
					var jxQuestion;
					var iIdx;
					for(var i=0; i<CLZ.oStore[argobj.pid].P.length; i++)
					{
						iIdx = +CLZ.oStore[argobj.pid].P[i];
						jxQuestion = $(oCurObj.data.jxQuestions[iIdx]);
						oQuestion = oCurObj.data.aQuestions[iIdx];
						CLZ.oStore[argobj.pid].Q[i] =
						{
							A:
							[
								{
									R: [],
									s: 0,
									d: 0,
									u: "i"
								}
							],
							O: [],
							P: [],
							R: [],
							s: 0,
							i: iIdx,
							y: CLD.CLTypeToAbbr[oQuestion.sType],
							u: (oCurObj.data.sDisplayAs=="list") ? "i" : "n"
						};
						if(oCurObj.data.bCredit)
						{
							CLZ.oStore[argobj.pid].Q[i].J = [];
							for(var j=0; j<oQuestion.aObjectives.length; j++)
							{
								CLZ.oStore[argobj.pid].Q[i].J.push( oQuestion.aObjectives[j].sId );
							}
						}
						if(oQuestion.bTimer && oCurObj.data.sDisplayAs!="list")
						{
							CLZ.oStore[argobj.pid].Q[i].t = oQuestion.iTimer;
							CLZ.oStore[argobj.pid].Q[i].e = 0;
						}
						switch(oQuestion.sType)
						{
							case "true-false":
							{
								CLZ.oStore[argobj.pid].Q[i].P.push( (jxQuestion.children("q_eval").text()=="true") ? 1 : 0 ); //no complex scoring
								if(jxQuestion.children("q_elements_order").text()=="random")
								{
									var aShuffled = [];
															for(var j=0; j<2; j++) aShuffled[j] = CLTOOLS.Dice({ iMax: 2, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 });
															for(var j=0; j<2; j++) CLZ.oStore[argobj.pid].Q[i].O.push(aShuffled[j]);
								}
								else
								{
									CLZ.oStore[argobj.pid].Q[i].O = [ 0, 1 ];
								}
								// always 1 attempt
								break;
							}
							case "choice":
							{
								for(var j=1; j<=oQuestion.jxVars.length; j++) // no complex scoring
								{
									if($(oQuestion.jxVars[j-1]).children("q_right").text()=="yes")
									{
										CLZ.oStore[argobj.pid].Q[i].P.push(j);
										break;
									}
								}
								if(oQuestion.bShuffle)
								{
									var aShuffled = [];
									for(var j=0; j<oQuestion.jxVars.length; j++) aShuffled.push( CLTOOLS.Dice({ iMax: oQuestion.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
									for(var j=0; j<oQuestion.jxVars.length; j++) CLZ.oStore[argobj.pid].Q[i].O.push(aShuffled[j]+1);
								}
								else
								{
									for(var j=1; j<=oQuestion.jxVars.length; j++)
									{
										CLZ.oStore[argobj.pid].Q[i].O.push(j);
									}
								}
								if(oCurObj.data.sDisplayAs!="list")
								{
									var iMaxAttepmts = 1;
									if(jxQuestion.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oQuestion.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +jxQuestion.children("q_attempts").text();
									}
									for(var j=1; j<iMaxAttepmts; j++)
									{
										CLZ.oStore[argobj.pid].Q[i].A.push({ u: "n" });
									}
								}
								break;
							}
							case "select":
							{
								for(var j=1; j<=oQuestion.jxVars.length; j++)
								{
									if($(oQuestion.jxVars[j-1]).children("q_right").text()=="yes")
									{
										CLZ.oStore[argobj.pid].Q[i].P.push(j);
									}
								}
								if(oQuestion.bShuffle)
								{
									var aShuffled = [];
									for(var j=0; j<oQuestion.jxVars.length; j++) aShuffled.push( CLTOOLS.Dice({ iMax: oQuestion.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
									for(var j=0; j<oQuestion.jxVars.length; j++) CLZ.oStore[argobj.pid].Q[i].O.push(aShuffled[j]+1);
								}
								else
								{
									for(var j=1; j<=oQuestion.jxVars.length; j++)
									{
										CLZ.oStore[argobj.pid].Q[i].O.push(j);
									}
								}
								if(oCurObj.data.sDisplayAs!="list")
								{
									var iMaxAttepmts = 1;
									if(jxQuestion.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oQuestion.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +jxQuestion.children("q_attempts").text();
									}
									for(var j=1; j<iMaxAttepmts; j++)
									{
										CLZ.oStore[argobj.pid].Q[i].A.push({ u: "n" });
									}
								}
								break;
							}
							case "order":
							case "oto":
							{
								for(var j=1; j<=oQuestion.jxVars.length; j++)
								{
									CLZ.oStore[argobj.pid].Q[i].P.push(j);
								}
								var aShuffled = [];
								for(var j=0; j<oQuestion.jxVars.length; j++) aShuffled.push( CLTOOLS.Dice({ iMax: oQuestion.jxVars.length, bIncludeMax: false, aUsed: aShuffled, iMaxTries: 1000 }) );
								for(var j=0; j<oQuestion.jxVars.length; j++)
								{
									CLZ.oStore[argobj.pid].Q[i].O.push(aShuffled[j]+1);
									CLZ.oStore[argobj.pid].Q[i].R.push(aShuffled[j]+1);
								}
								if(oCurObj.data.sDisplayAs!="list")
								{
									var iMaxAttepmts = 1;
									if(jxQuestion.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oQuestion.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +jxQuestion.children("q_attempts").text();
									}
									for(var j=1; j<iMaxAttepmts; j++)
									{
										CLZ.oStore[argobj.pid].Q[i].A.push({ u: "n" });
									}
								}
								break;
							}
							case "numeric":
							case "fib":
							{
								var oPattern;
								var aConds;
								var jVar;
								var jConds;
								for(var j=1; j<=oQuestion.jxVars.length; j++)
								{
									jVar = $(oQuestion.jxVars[j-1]);
									jConds = jVar.find("q_conditions > item");
									oPattern = { sOp: jVar.children("q_cond_logic").text() };
									if(jConds.length != 0)
									{
                                        aConds = [];
										if (oQuestion.sType == "fib") {
											for (var k = 0; k < jConds.length; k++)
                                            {
												aConds.push({
													op: $(jConds[k]).children("q_condition").text(),
													val: $(jConds[k]).children("q_value").text(),
													cs: ($(jConds[k]).children("q_case").text() == "yes")
												})
                                            }
                                        }
                                        else
                                        {
											for (var k = 0; k < jConds.length; k++)
                                            {
												aConds.push({
													op: $(jConds[k]).children("q_condition").text(),
													val: +$(jConds[k]).children("q_value").text()
												})
                                            }
                                        }
                                        oPattern.aConds = aConds.slice(0);
                                        CLZ.oStore[argobj.pid].Q[i].P.push(oPattern);
                                    }
									CLZ.oStore[argobj.pid].Q[i].R.push("");
								}
								for(var j=1; j<=oQuestion.jxVars.length; j++)
								{
									CLZ.oStore[argobj.pid].Q[i].O.push(j);
								}
								if(oCurObj.data.sDisplayAs!="list")
								{
									var iMaxAttepmts = 1;
									if(jxQuestion.children("q_attempts_type").text()=="auto")
									{
										iMaxAttepmts = oQuestion.jxVars.length - 1;
									}
									else
									{
										iMaxAttepmts = +jxQuestion.children("q_attempts").text();
									}
									for(var j=1; j<iMaxAttepmts; j++)
									{
										CLZ.oStore[argobj.pid].Q[i].A.push({ u: "n" });
									}
								}
								break;
							}
						}
					}
				}
				jCurObjDiv.find(".q-feedback-btn").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				oCurObj.data.bCreated = true;
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Init" });
				}
				return true;
			}

function Init(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.bInit==true)
				{
					return false;
				}
				var jCurObjDiv = $(oCurObj.div);
				var iQId;
				var jxQuestion;
				var sIntId;
				var oStore = CLZ.oStore[argobj.pid];
				var oQStore;
				switch(oCurObj.data.sDisplayAs)
				{
					case "page":
					{
						switch(oCurObj.data.sMode)
						{
							case "init":
							{
								for(var i=0; i<oStore.P.length; i++)
								{
									iQId = +oStore.P[i];
									for(var j=0; j<oStore.Q.length; j++)
									{
										if(+oStore.Q[j].i==iQId)
										{
											oQStore = oStore.Q[j];
											break;
										}
									}
									oQStore = oStore.Q[i];
									oCurObj.CallMethod({ sMethod: "AppendQuestion", oMethodArgs: { qid: iQId, iNum: (i+1) } });
								}
								oCurObj.CallMethod({ sMethod: "InitRail" });
								oCurObj.CallMethod({ sMethod: "AdjustArea" });
								oCurObj.CallMethod({ sMethod: "DisplayQuestion", oMethodArgs: { qid: oStore.P[0] } });
								oCurObj.CallMethod({ sMethod: "UpdateRail" });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
								if(oCurObj.data.bTestTimer)
								{
									oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "init" } });
								}
								jCurObjDiv.find(".cl-btn, .cl-t-progress-item-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								oCurObj.data.bInit = true;
								break;
							}
							case "continue":
							{
								var oQStore;
								for(var i=0; i<oStore.P.length; i++)
								{
									iQId = +oStore.P[i];
									for(var j=0; j<oStore.Q.length; j++)
									{
										if(+oStore.Q[j].i==iQId)
										{
											oQStore = oStore.Q[j];
											break;
										}
									}
									oCurObj.CallMethod({ sMethod: "AppendQuestion", oMethodArgs: { qid: iQId, iNum: (i+1) } });
									if(oQStore.u!="n")
									{
										oCurObj.CallMethod({ sMethod: "RestoreQuestionState", oMethodArgs: { qid: iQId } });
										if(oQStore.u=="c")
										{
											oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: [ iQId ] } });
										}
									}
								}
								oCurObj.CallMethod({ sMethod: "AdjustArea" });
								var iFirstUnfinished = oCurObj.CallMethod({ sMethod: "FirstUnfinished" });
								if(iFirstUnfinished==-1)
								{
									oCurObj.CallMethod({ sMethod: "DisplayQuestion", oMethodArgs: { qid: oStore.P[0] } });
								}
								else
								{
									oCurObj.CallMethod({ sMethod: "DisplayQuestion", oMethodArgs: { qid: oStore.P[iFirstUnfinished] } });
								}
								oCurObj.CallMethod({ sMethod: "InitRail" });
								oCurObj.CallMethod({ sMethod: "UpdateRail" });
								if(oCurObj.data.bTestTimer)
								{
									oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "proceed" } });
								}
								jCurObjDiv.find(".cl-btn, .cl-t-progress-item-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
								oCurObj.data.bInit = true;
								break;
							}
							case "browse":
							{
								for(var i=0; i<oStore.P.length; i++)
								{
									iQId = +oStore.P[i];
									oCurObj.CallMethod({ sMethod: "AppendQuestion", oMethodArgs: { qid: iQId, iNum: (i+1) } });
									oCurObj.CallMethod({ sMethod: "RestoreQuestionState", oMethodArgs: { qid: iQId } });
								}
								oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: oStore.P }});
								oCurObj.CallMethod({ sMethod: "AdjustArea" });
								oCurObj.CallMethod({ sMethod: "DisplayQuestion", oMethodArgs: { qid: oStore.P[0] } });
								oCurObj.CallMethod({ sMethod: "InitRail" });
								oCurObj.CallMethod({ sMethod: "UpdateRail" });
								if(oCurObj.data.bTestTimer)
								{
									oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "restore" } });
								}
								jCurObjDiv.find(".cl-t-progress-item-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
								jCurObjDiv.find(".cl-t-footer-cell-center").html((oCurObj.data.sTestResult=="t") ? oCurObj.jxParams.find("t_stop_msg").text() : oCurObj.jxParams.find("t_allanswered_msg").text());
								oCurObj.data.bInit = true;
								break;
							}
						}
						break;
					}
					case "list":
					{
						switch(oCurObj.data.sMode)
						{
							case "init":
							{
								for(var i=0; i<oStore.P.length; i++)
								{
									iQId = +oStore.P[i];
									for(var j=0; j<oStore.Q.length; j++)
									{
										if(+oStore.Q[j].i==iQId)
										{
											oQStore = oStore.Q[j];
											break;
										}
									}
									oCurObj.CallMethod({ sMethod: "AppendQuestion", oMethodArgs: { qid: iQId, iNum: (i+1) } });
									if(oQStore.u=="n")
									{
										oQStore.u = "i";
										oQStore.s = 0;
										oQStore.A[0].s = 0;
										oQStore.A[0].u = "i";
									}
								}
								oCurObj.CallMethod({ sMethod: "InitRail" });
								oCurObj.CallMethod({ sMethod: "AdjustArea" });
								if(oCurObj.jxParams.children("allow_partial_answer").text()!="yes" || oCurObj.data.sDisplayAs=="page")
								{
									oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								}
								if(oCurObj.data.bTestTimer)
								{
									oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "init" } });
								}
								jCurObjDiv.find(".cl-btn, .cl-t-progress-item-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								break;
							}
							case "continue":
							{
								var iFirstUnanswered = oCurObj.CallMethod({ sMethod: "FirstUnanswered" });
								for(var i=0; i<oStore.P.length; i++)
								{
									iQId = +oStore.P[i];
									for(var j=0; j<oStore.Q.length; j++)
									{
										if(+oStore.Q[j].i==iQId)
										{
											oQStore = oStore.Q[j];
											break;
										}
									}
									oCurObj.CallMethod({ sMethod: "AppendQuestion", oMethodArgs: { qid: iQId, iNum: (i+1) } });
									if(oQStore.u!="n")
									{
										oCurObj.CallMethod({ sMethod: "RestoreQuestionState", oMethodArgs: { qid: iQId } });
									}
								}
								oCurObj.CallMethod({ sMethod: "InitRail" });
								oCurObj.CallMethod({ sMethod: "UpdateRail" });
								oCurObj.CallMethod({ sMethod: "AdjustArea" });
								if(oCurObj.jxParams.children("allow_partial_answer").text()!="yes" || oCurObj.data.sDisplayAs=="page")
								{
									oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								}
								if(oCurObj.data.bTestTimer)
								{
									oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "proceed" } });
								}
								jCurObjDiv.find(".cl-btn, .cl-t-progress-item-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								if(iFirstUnanswered!=-1)
								{
									oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { qid: iFirstUnanswered } });
								}
								break;
							}
							case "browse":
							{
								for(var i=0; i<oStore.P.length; i++)
								{
									iQId = +oStore.P[i];
									for(var j=0; j<oStore.Q.length; j++)
									{
										if(+oStore.Q[j].i==iQId)
										{
											oQStore = oStore.Q[j];
											break;
										}
									}
									oCurObj.CallMethod({ sMethod: "AppendQuestion", oMethodArgs: { qid: iQId, iNum: (i+1) } });
									oCurObj.CallMethod({ sMethod: "RestoreQuestionState", oMethodArgs: { qid: iQId } });
								}
								oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: oStore.P } });
								oCurObj.CallMethod({ sMethod: "InitRail" });
								oCurObj.CallMethod({ sMethod: "UpdateRail" });
								oCurObj.CallMethod({ sMethod: "AdjustArea" });
								if(oCurObj.data.bTestTimer)
								{
									oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "restore" } });
								}
								jCurObjDiv.find(".cl-t-progress-item-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								jCurObjDiv.find(".cl-t-footer-cell-center").html((oCurObj.data.sTestResult=="t") ? oCurObj.jxParams.find("t_stop_msg").text() : oCurObj.jxParams.find("t_allanswered_msg").text());
								break;
							}
						}
						break;
					}
				}
				return true;
			}

function AppendQuestion(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jxQuestion = $(oCurObj.data.jxQuestions[+argobj.qid]);
				var oQStore = CLZ.oStore[argobj.pid].Q[+argobj.iNum-1];
				var oQuestion = oCurObj.data.aQuestions[+oQStore.i];
				var sQType = oQuestion.sType;
				var jQuestion = $(document.createElement("div")).addClass("q-question").attr({ "id": argobj.pid + "_Q_" + argobj.qid, "data-q-num": argobj.qid }).appendTo(jCurObjDiv.find(".cl-t-workarea"));
				if(oCurObj.data.sDisplayAs=="list")
				{
					var sString = oCurObj.jxParams.children("string_question_count").text();
					var aParts = sString.split("{{1}}");
					sString = aParts.join(argobj.iNum.toString());
					aParts = sString.split("{{2}}");
					sString = aParts.join(CLZ.oStore[argobj.pid].P.length.toString());
					jCurObjDiv.find(".template-storage div[data-template='qnumber']").clone(true).removeAttr("data-template").html(sString).appendTo(jQuestion);
				}
				if(jxQuestion.children("display_question_text").text()=="yes")
				{
					jCurObjDiv.find(".template-storage div[data-template='qtext']").clone(true).removeAttr("data-template").html(jxQuestion.children("text_main").text() ).appendTo(jQuestion);
				}
				if(oCurObj.data.bDisplayGoal)
				{
					jCurObjDiv.find(".template-storage div[data-template='qgoal']").clone(true).removeAttr("data-template").html( oCurObj.jxParams.children("q_goal_" + sQType).text() ).appendTo(jQuestion);
				}
				switch(sQType)
				{
					case "true-false":
					{
						var sLayout = jxQuestion.children("q_elements_layout").text();
						var jQBlock = jCurObjDiv.find(".template-storage div[data-template='qvars-true-false-" + sLayout + "']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-selection": "" }).appendTo(jQuestion);
						var sOrder = jxQuestion.children("q_elements_order").text();
						if(sOrder=="random")
						{
							sOrder = (Math.round(Math.random())==1) ? "tf" : "ft";
						}
						var jVar;
						if(sLayout=="v")
						{
							var jPlaces = jQBlock;
							if(sOrder=="tf")
							{
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "1" }).appendTo(jPlaces[0]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_true").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "0" }).appendTo(jPlaces[0]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_false").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							}
							else
							{
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "1" }).appendTo(jPlaces[0]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_false").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "0" }).appendTo(jPlaces[0]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_true").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							}
						}
						else
						{
							var jPlaces = jQBlock.find("*[data-append]");
							if(sOrder=="tf")
							{
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "1" }).appendTo(jPlaces[0]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_true").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "0" }).appendTo(jPlaces[1]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_false").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							}
							else
							{
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "0" }).appendTo(jPlaces[0]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_false").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
								jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-tf']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": "1" }).appendTo(jPlaces[1]);
								jVar.find(".text-main").html( jxQuestion.children("q_text_true").text() );
								jVar.addClass("var-clickable").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							}
						}
						break;
					}
					case "choice":
					case "select":
					{
						var jxVars = jxQuestion.find("q_variants_" + sQType + " > item");
						var jQBlock = jCurObjDiv.find(".template-storage div[data-template='qvars-" + sQType + "']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-selection": "" }).appendTo(jQuestion);
						var jVar;
						for(var i=0; i<oQStore.O.length; i++)
						{
							jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-" + sQType + "']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": oQStore.O[i] }).appendTo(jQBlock);
							jVar.find(".text-main").html( $(jxVars[+oQStore.O[i] - 1]).children("q_variant").text() );
							jVar.addClass("var-clickable").on("click", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
						}
						break;
					}
					case "numeric":
					case "fib":
					{
						var jxVars = jxQuestion.find("q_variants_" + sQType + " > item");
						var jQBlock = jCurObjDiv.find(".template-storage div[data-template='qvars-" + sQType + "']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid }).appendTo(jQuestion);
						var jVar;
						for(var i=0; i<jxVars.length; i++)
						{
							jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-" + sQType + "']").clone(true).appendTo(jQBlock);
							jVar.removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": (i+1) });
							jVar.find(".text-main").html($(jxVars[i]).children("q_explanation").text());
							jVar.find("input").on("keyup", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("blur", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
							if($(jxVars[i]).children("q_exp_position").length!=0)
							{
								if($(jxVars[i]).children("q_exp_position").text()=="left")
								{
									jVar.find(".cl-var-exp-right").remove();
									jVar.find(".var-spot-td").addClass("cl-var-input-cell-left");
								}
								else
								{
									jVar.find(".cl-var-exp-left").remove();
									jVar.find(".var-spot-td").addClass("cl-var-input-cell-right");
								}
							}
							else
							{
								jVar.find(".cl-var-exp-left").remove();
								jVar.find(".var-spot-td").addClass("cl-var-input-cell-right");
							}
						}
						break;
					}
					case "order":
					{
						var jxVars = jxQuestion.find("q_variants_order > item");
						var jQBlock = jCurObjDiv.find(".template-storage div[data-template='qvars-order']").clone(true).removeAttr("data-template").attr({ "id": argobj.pid + "_" + argobj.qid + "_VC", "data-type": sQType, "data-qid": argobj.qid }).appendTo(jQuestion);
						var jVar;
						for(var i=0; i<oQStore.O.length; i++)
						{
							jVar = jCurObjDiv.find(".template-storage div[data-template='qvar-order']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": oQStore.O[i] }).appendTo(jQBlock);
							jVar.find(".text-main").html( $(jxVars[oQStore.O[i]-1]).children("q_variant").text() );
														jVar.addClass("unselectable cl-drag var-order-elem").draggable({ snap: false, scope: argobj.pid + "_" + argobj.qid, refreshPositions: true, containment: $("#" + argobj.pid + "_" + argobj.qid + "_VC"), axis: "y", stack: ".var-order-elem" }).droppable({ accept: ".var-order-elem", scope: argobj.pid + "_" + argobj.qid, tolerance: "pointer" }).on("drop", function(event, ui) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: event, elem: this, bullet: ui.draggable } }) });
						}
						var jVars = jQBlock.find(".var");
						if(jVars.length!=0)
						{
							var iLeft = $(jVars[0]).position().left;
							var aTops = [];
							jVars.each(function ()
							{
								aTops.push($(this).position().top);
							});
							jVars.attr({ "data-tops": aTops.join("|") });
							jQBlock.attr({ "data-order": oQStore.O.join("|") });
							jQBlock.css({ "height": jQBlock.height() + "px" });
						}
						break;
					}
					case "oto":
					{
						var jxVars = jxQuestion.find("q_variants_" + sQType + " > item");
						var jQBlock = jCurObjDiv.find(".template-storage div[data-template='qvars-" + sQType + "']").clone(true).removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid }).appendTo(jQuestion);

						var jTable = $(document.createElement("table")).addClass("oto-table").appendTo(jQBlock);
						var jTR;
						var jTD;
						var jTarget;
						var jBullet;
						for(var i=0; i<jxVars.length; i++)
						{
							jTR = $(document.createElement("tr")).appendTo(jTable);
							jTD = $(document.createElement("td")).addClass("left-part").appendTo(jTR);
							jTarget = jCurObjDiv.find(".template-storage div[data-template='qvar-oto-target']").clone(true).appendTo(jTD);
							jTarget.removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": (i+1) });
							jTarget.find(".text-main").html($(jxVars[i]).children("q_target").text());
							jTD = $(document.createElement("td")).addClass("center-part").appendTo(jTR);
							jTD.append( $(document.createElement("div")).addClass("oto-connector").html("&nbsp;") );
							jTD = $(document.createElement("td")).addClass("right-part").appendTo(jTR);
							jBullet = jCurObjDiv.find(".template-storage div[data-template='qvar-oto-bullet']").clone(true).appendTo(jTD);
							jBullet.removeAttr("data-template").attr({ "data-type": sQType, "data-qid": argobj.qid, "data-vid": (i+1) });
							jBullet.find(".text-main").html($(jxVars[i]).children("q_bullet").text());
						}

						// resize and distribute
						var jTargets = jQBlock.find(".var-target");
						var jBullets = jQBlock.find(".var-bullet");
						jTargets.each(function () { $(this).css({ "width": $(this).width + "px" }) });
						jBullets.each(function () { $(this).css({ "width": $(this).width + "px" }) });
						var iHMax = 0;
						jTargets.each(function () { if($(this).find(".target-text-cell").height() > iHMax) { iHMax = $(this).find(".target-text-cell").height() }  });
						jBullets.each(function () { if($(this).find(".bullet-text-cell").height() > iHMax) { iHMax = $(this).find(".bullet-text-cell").height() }  });
						jTargets.find(".target-text-cell").height(iHMax);
						jBullets.find(".bullet-text-cell").height(iHMax);
						jTargets.each(function () { $(this).height($(this.height)) });
						var aTops = [];
						jBullets.each(function ()
						{
							$(this).css( { "height": $(this).height + "px", "width": $(this).width() + "px" });
							var oPos = $(this).position();
													aTops.push(oPos.top/CLZ.nZoom);
							var jParent = $(this).parent();
													$(this).css({ "position": "absolute", "left": (oPos.left/CLZ.nZoom) + "px", "top": aTops[aTops.length-1] + "px", "z-index": 1000 }).appendTo(jQBlock);
													$(this).addClass("unselectable cl-drag").draggable({ snap: false, refreshPositions: true, containment: jQBlock, axis: "y", stack: ".var-bullet" }).droppable({ accept: ".var-bullet", tolerance: "pointer" }).on("drop", function(event, ui) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: event, elem: this, bullet: ui.draggable } }) });
						});
						jBullets.attr({ "data-tops": aTops.join("|") });
						for(var i=0; i<jBullets.length; i++)
						{
							$(jBullets[i]).css({ "top": aTops[+oQStore.O[i]-1] });
						}
						$(jBullets[0]).parent().attr({ "data-order": oQStore.O.join("|") });
						break;
					}
				}
				return true;
			}

function DisplayQuestion(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				jCurObjDiv.find(".q-question").hide();
				jCurObjDiv.find(".q-question[data-q-num='" + argobj.qid + "']").show();
				var iNumber = -1;
				var oQStore;
				for(var i=0; i<CLZ.oStore[argobj.pid].Q.length; i++)
				{
					if(+CLZ.oStore[argobj.pid].Q[i].i===argobj.qid)
					{
						iNumber = i;
						oQStore = CLZ.oStore[argobj.pid].Q[i];
						break;
					}
				}
				if(iNumber==-1) return false;
				var oQuestion = oCurObj.data.aQuestions[+oQStore.i];
				if(oQStore.u=="n") oQStore.u = "i";
				if(oQStore.s==0)
				{
					oQStore.s = (new Date()).valueOf() - CLZ.oStore[argobj.pid].s;
					if(oQuestion.bTimer)
					{
						oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { qid: argobj.qid, sAction: "init" } });
					}
					else
					{
						oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { qid: argobj.qid, sAction: "hide" } });
					}
				}
				else
				{
					oCurObj.CallMethod({ sMethod: "RestoreQuestionState", oMethodArgs: { qid: argobj.qid, inum: iNumber } });
					if(oQuestion.bTimer)
					{
						oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { qid: argobj.qid, sAction: (oQStore.u=="c" ? "restore" : "proceed") } });
					}
					else
					{
						oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { qid: argobj.qid, sAction: "hide" } });
					}
				}
				var iAtt = 0;
				for(var i=0; i<oQStore.A.length; i++)
				{
					if(oQStore.A[i].u!="c")
					{
						iAtt = i + 1;
						break;
					}
				}
				var sString = oCurObj.jxParams.children("string_question_attempts").text();
				var aParts = sString.split("{{2}}");
				sString = aParts.join(oQStore.A.length);
				aParts = sString.split("{{1}}");
				sString = aParts.join(iAtt);
				jCurObjDiv.find(".cl-t-attempts").html(sString);
				oCurObj.CallMethod({ sMethod: "UpdateRail" });
				oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
				if(oCurObj.data.sMode=="browse")
				{
					oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: [ argobj.qid ] } });
				}
				oCurObj.FireEvent({ sName: "onDisplayQuestion" });
				return true;
			}

function DisableQuestions(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jQuestion;
				var iIdx;
				var jQVars;
				var sQType;
				var jVars;
				for(var i=0; i<argobj.aTargets.length; i++)
				{
					iIdx = argobj.aTargets[i];
					jQuestion = jCurObjDiv.find(".q-question[data-q-num='" + iIdx + "']");
					if(jQuestion.hasClass("q-disabled"))
					{
						continue;
					}
					jQVars = jQuestion.find(".q-vars:first");
					sQType = jQVars.attr("data-type");
					switch(sQType)
					{
						case "true-false":
						case "choice":
						case "select":
						{
							jQVars.find(".var").removeClass("var-clickable").addClass("var-disabled");
							break;
						}
						case "order":
						{
							jQVars.find(".var").removeClass("cl-drag").addClass("var-disabled").draggable("destroy").droppable("destroy");
							break;
						}
						case "oto":
						{
							jQVars.find(".var-bullet").removeClass("cl-drag").addClass("var-disabled").draggable("destroy").droppable("destroy");
							break;
						}
						case "numeric":
						case "fib":
						{
							jQVars.find(".var-input").prop("disabled", true);
							break;
						}
					}
					jQuestion.addClass("q-disabled");
				}
				return true;
			}

function GoTo(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var iIdx = argobj.qid;
				var jQuestion;
				oCurObj.CallMethod({ sMethod: "Feedback" });
				if(iIdx==null)
				{
					if(argobj.sDir==null || oCurObj.data.sDisplayAs=="list")
					{
						return false;
					}
					jQuestion = jCurObjDiv.find(".q-question:visible");
					iIdx = +jQuestion.attr("data-q-num");
					var i = $.inArray(iIdx, CLZ.oStore[argobj.pid].P);
					switch(argobj.sDir)
					{
						case "next":
						{
							if(i>=CLZ.oStore[argobj.pid].P.length-1)
							{
								i = oCurObj.CallMethod({ sMethod: "FirstUnfinished" });
								if((i>=CLZ.oStore[argobj.pid].P.length-1) || i<=0)
								{
									return false;
								}
								iIdx = CLZ.oStore[argobj.pid].P[i];
							}
							else
							{
								iIdx = CLZ.oStore[argobj.pid].P[i+1];
							}
							break;
						}
						case "back":
						{
							if(i<=0)
							{
								i = oCurObj.CallMethod({ sMethod: "FirstUnfinished" });
								if((i>=CLZ.oStore[argobj.pid].P.length-1) || i<=0)
								{
									return false;
								}
								iIdx = CLZ.oStore[argobj.pid].P[i];
							}
							else
							{
								iIdx = CLZ.oStore[argobj.pid].P[i-1];
							}
							break;
						}
					}
					if(iIdx==null) return false;
				}
				jQuestion = jCurObjDiv.find(".q-question[data-q-num='" + iIdx + "']");
				switch(oCurObj.data.sDisplayAs)
				{
					case "page":
					{
						oCurObj.CallMethod({ sMethod: "DisplayQuestion", oMethodArgs: { qid: iIdx } });
						break;
					}
					case "list":
					{
						var jWA = jCurObjDiv.find(".cl-t-workarea");
						jWA.animate({ scrollTop: jQuestion[0].offsetTop }, 400);
						break;
					}
				}
				return true;
			}

function AdjustArea(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jWA = jCurObjDiv.find(".cl-t-workarea");
				if(oCurObj.data.sDisplayAs=="list")
				{
					var jContainer = jCurObjDiv.find(".cl-t-container");
					var jHeader = jCurObjDiv.find(".cl-t-header");
					var jFooter = jCurObjDiv.find(".cl-t-footer");
					var iFullH = parseInt(oCurObj.initial.h, 10);
					var iBorder = parseInt(window.getComputedStyle(jContainer[0], null).getPropertyValue("border-top-width"), 10) + parseInt(window.getComputedStyle(jContainer[0], null).getPropertyValue("border-bottom-width"), 10);
					iBorder += parseInt(window.getComputedStyle(jHeader[0], null).getPropertyValue("border-top-width"), 10) + parseInt(window.getComputedStyle(jHeader[0], null).getPropertyValue("border-bottom-width"), 10);
					iBorder += parseInt(window.getComputedStyle(jWA[0], null).getPropertyValue("border-top-width"), 10) + parseInt(window.getComputedStyle(jWA[0], null).getPropertyValue("border-bottom-width"), 10);
					iBorder += parseInt(window.getComputedStyle(jFooter[0], null).getPropertyValue("border-top-width"), 10) + parseInt(window.getComputedStyle(jFooter[0], null).getPropertyValue("border-bottom-width"), 10);
					var iPadding = parseInt(window.getComputedStyle(jWA[0], null).getPropertyValue("padding-top"), 10) + parseInt(window.getComputedStyle(jWA[0], null).getPropertyValue("padding-bottom"), 10);
					var iH = iFullH - jHeader[0].offsetHeight - jFooter[0].offsetHeight - iPadding - iBorder;
					jWA.css({ "height": iH + "px" });
				}
				else if(oCurObj.data.sDisplayAs=="page")
				{
					if(oCurObj.jxParams.children("adjust_height").text()=="yes")
					{
						var jQuestions = jCurObjDiv.find(".q-question");
						var iMaxH = 0;
						jQuestions.each(function ()
						{
							var iQH = parseInt(window.getComputedStyle(this, null).getPropertyValue("height"), 10);
							if(iQH > iMaxH)
							{
								iMaxH = iQH;
							}
						});
						jWA.css({ "height": iMaxH + "px" });
					}
				}
				return true;
			}

function InitRail(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jRailItem = jCurObjDiv.find(".cl-t-progress-item");
				var iQNum;
				var sString = oCurObj.jxParams.children("string_question_count").text();
				var aParts = sString.split("{{2}}");
				sString = aParts.join(CLZ.oStore[argobj.pid].P.length.toString());
				for(var i=0; i<CLZ.oStore[argobj.pid].P.length; i++)
				{
					iQNum = CLZ.oStore[argobj.pid].P[i];
					$(jRailItem[i]).attr({ "data-q-num": iQNum });
					aParts = sString.split("{{1}}");
					$(jRailItem[i]).children(".cl-t-progress-item-btn").attr({ "title": aParts.join((i+1).toString()) });
				}
				return true;
			}

function UpdateRail(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jQuestions = jCurObjDiv.find("div.q-question");
				var aTouched = [];
				var iIdx = -1;
				var oStore = CLZ.oStore[argobj.pid];
				var oQStore;
				for(var i=0; i<oStore.P.length; i++)
				{
					iIdx = +oStore.P[i];
					for(var j=0; j<oStore.Q.length; j++)
					{
						if(+oStore.Q[j].i==iIdx)
						{
							oQStore = oStore.Q[j];
							break;
						}
					}
					if(oQStore.A[0].R!=null)
					{
						if(oQStore.A[0].R.length!=0)
						{
							aTouched.push(iIdx);
						}
					}
				}
				var jRailItem = jCurObjDiv.find(".cl-t-progress-item");
				jRailItem.each(function ()
				{
					if($.inArray(+$(this).attr("data-q-num"), aTouched)!=-1)
					{
						$(this).removeClass("cl-unvisited cl-current").addClass("cl-visited");
					}
					else
					{
						$(this).removeClass("cl-visited cl-current").addClass("cl-unvisited");
					}
				});
				var sString;
				var aParts;
				if(oCurObj.data.sDisplayAs=="page")
				{
					var iIdx = +jQuestions.filter(":visible").attr("data-q-num")
					jCurObjDiv.find(".cl-t-progress-item[data-q-num='" + iIdx + "']").removeClass("cl-visited cl-unvisited").addClass("cl-current");
					sString = oCurObj.jxParams.children("string_question_count").text();
					aParts = sString.split("{{2}}");
					sString = aParts.join(oStore.P.length);
					aParts = sString.split("{{1}}");
					iIdx = $.inArray(iIdx, oStore.P) + 1;
					sString = aParts.join(iIdx);
					jCurObjDiv.find(".cl-t-info-line").html(sString);
				}
				sString = oCurObj.jxParams.children("string_answered").text();
				aParts = sString.split("{{1}}");
				sString = aParts.join(aTouched.length);
				aParts = sString.split("{{2}}");
				sString = aParts.join(oStore.P.length);
				jCurObjDiv.find(".cl-t-answered-info").html(sString);
				if(aTouched.length>=oStore.P.length)
				{
					oCurObj.data.bAllAnswered = true;
					if(oCurObj.data.sDisplayAs=="list")
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
					}
				}
				return true;
			}

function RestoreQuestionState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jxQuestion = $(oCurObj.data.jxQuestions[argobj.qid]);
				var iIdx = $.inArray(+argobj.qid, CLZ.oStore[argobj.pid].P);
				var oQStore = CLZ.oStore[argobj.pid].Q[iIdx];
				if(oQStore==null) return false;
				var oQuestion = oCurObj.data.aQuestions[+oQStore.i];
				var sQType = oQuestion.sType;
				if(argobj.bCorrect==true)
				{
					var jQuestion = jCurObjDiv.find(".q-question[data-q-num='" + argobj.qid + "']");
					var jQVars = jQuestion.find(".q-vars:first");
					jQuestion.find(".q-question-goal").html( oCurObj.jxParams.find("q_goal_right").text() );
					switch(sQType)
					{
						case "true-false":
						case "choice":
						{
							for(var i=0; i<oQStore.P.length; i++)
							{
								oCurObj.CallMethod({ sMethod: "HandleSpot", oMethodArgs: { jElem: jQuestion.find(".var[data-vid='" + oQStore.P[i] + "']") } });
							}
							break;
						}
						case "select":
						{
							var jElem;
							var iInPattern = -1;
							for(var i=0; i<oQStore.O.length; i++)
							{
								jElem = jQuestion.find(".var[data-vid='" + oQStore.O[i] + "']");
								iInPattern = $.inArray(+oQStore.O[i], oQStore.P);
								if(jElem.hasClass("var-selected"))
								{
									if(iInPattern==-1)
									{
										oCurObj.CallMethod({ sMethod: "HandleSpot", oMethodArgs: { jElem: jElem } });
									}
								}
								else
								{
									if(iInPattern!=-1)
									{
										oCurObj.CallMethod({ sMethod: "HandleSpot", oMethodArgs: { jElem: jElem } });
									}
								}
							}
							break;
						}
						case "order":
						{
							var jParent = jQuestion.find(".var").parent();
							for(var i=0; i<oQStore.P.length; i++)
							{
								jQuestion.find(".var[data-vid='" + oQStore.P[i] + "']").appendTo(jParent);
							}
							jQVars.attr({ "data-order": oQStore.P.join("|"), "data-touched": "1" });
							break;
						}
						case "oto":
						{
							var jBullets = jQuestion.find(".var-bullet");
							var jParent = $(jBullets[0]).parent();
							var aTops = $(jBullets[0]).attr("data-tops").split("|");
							for(var i=0; i<oQStore.R.length; i++)
							{
								jParent.children(".var-bullet[data-vid='"+ oQStore.P[i] +"']").css({ "top": aTops[i] + "px" });
							}
							jQVars.attr({ "data-order": oQStore.P.join("|"), "data-touched": "1" });
							break;
						}
						case "numeric":
						{
							var aParts;
							var sAnswerString = "";
							var oSigns = { "eq": "=", "gt": ">", "gte": ">=", "lt": "<", "lte": "<=", "ne": "!=" };
							var aAnswer = [];
							var aStrings = [];
							for(var i=0; i<oQStore.P.length; i++)
							{
								sAnswerString = "";
								if(oQStore.P[i].aConds.length==0)
								{
									sAnswerString = "*";
								}
								else
								{
									aStrings = [];
									for(var j=0; j<oQStore.P[i].aConds.length; j++)
									{
										aStrings.push(oSigns[oQStore.P[i].aConds[j].op] + oQStore.P[i].aConds[j].val);
									}
									sAnswerString += aStrings.join(" " + oQStore.P[i].sOp + " ");
								}
								jQuestion.find(".var[data-vid='" + (i+1) + "'] .var-input").val(sAnswerString);
							}
							jQVars.attr({ "data-touched": "1" });
							break;
						}
						case "fib":
						{
							var aParts;
							var sAnswerString = "";
							var aAnswer = [];
							var aStrings = [];
							for(var i=0; i<oQStore.P.length; i++)
							{
								sAnswerString = "";
								if(oQStore.P[i].aConds.length==0)
								{
									sAnswerString = "*";
								}
								else
								{
									aStrings = [];
									for(var j=0; j<oQStore.P[i].aConds.length; j++)
									{
										switch(oQStore.P[i].aConds[j].op)
										{
											case "eq":
											{
												aStrings.push("=" + oQStore.P[i].aConds[j].val);
												break;
											}
											case "ee":
											{
												aStrings.push("*" + oQStore.P[i].aConds[j].val);
												break;
											}
											case "bw":
											{
												aStrings.push(oQStore.P[i].aConds[j].val + "*");
												break;
											}
											case "cn":
											{
												aStrings.push("*" + oQStore.P[i].aConds[j].val + "*");
												break;
											}
										}
									}
									sAnswerString += aStrings.join(" " + oQStore.P[i].sOp + " ");
								}
								jQuestion.find(".var[data-vid='" + (i+1) + "'] .var-input").val(sAnswerString);
							}
							jQVars.attr({ "data-touched": "1" });
							break;
						}
					}
				}
				else if(oQStore.R.length!=0)
				{
					var jQuestion = jCurObjDiv.find(".q-question[data-q-num='" + argobj.qid + "']");
					var jQVars = jQuestion.find(".q-vars:first");
					switch(sQType)
					{
						case "true-false":
						case "choice":
						{
							for(var i=0; i<oQStore.R.length; i++)
							{
								oCurObj.CallMethod({ sMethod: "HandleSpot", oMethodArgs: { jElem: jQuestion.find(".var[data-vid='" + oQStore.R[i] + "']") } });
							}
							break;
						}
						case "select":
						{
							var jElem;
							for(var i=0; i<oQStore.R.length; i++)
							{
								jElem = jQuestion.find(".var[data-vid='" + oQStore.R[i] + "']");
								if(!jElem.hasClass("var-selected"))
								{
									oCurObj.CallMethod({ sMethod: "HandleSpot", oMethodArgs: { jElem: jElem } });
								}
							}
							break;
						}
						case "order":
						{
							var jParent = jQuestion.find(".var").parent();
							for(var i=0; i<oQStore.R.length; i++)
							{
								jQuestion.find(".var[data-vid='" + oQStore.R[i] + "']").appendTo(jParent);
							}
							jQVars.attr({ "data-order": oQStore.R.join("|"), "data-touched": "1" });
							break;
						}
						case "oto":
						{
							var jBullets = jQuestion.find(".var-bullet");
							var jParent = $(jBullets[0]).parent();
							var aTops = $(jBullets[0]).attr("data-tops").split("|");
							for(var i=0; i<oQStore.R.length; i++)
							{
								jParent.children(".var-bullet[data-vid='"+ oQStore.R[i] +"']").css({ "top": aTops[i] + "px" });
							}
							jQVars.attr({ "data-order": oQStore.R.join("|"), "data-touched": "1" });
							break;
						}
						case "numeric":
						case "fib":
						{
							var aParts;
							for(var i=0; i<oQStore.R.length; i++)
							{
								jQuestion.find(".var[data-vid='" + (i+1) + "'] .var-input").val(oQStore.R[i]);
							}
							jQVars.attr({ "data-touched": "1" });
							break;
						}
					}
				}
				return true;
			}

function Button(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jBtn = jCurObjDiv.find(".cl-btn").filter("[data-action='" + argobj.sBtn + "']");
				switch(argobj.sAction)
				{
					case "init":
					{
						jBtn.on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
						break;
					}
					case "disable":
					{
						jBtn.removeClass("btn-idle").addClass("btn-disabled");
						break;
					}
					case "enable":
					{
						jBtn.show().removeClass("btn-disabled").addClass("btn-idle");
						break;
					}
					case "hide":
					{
						jBtn.hide();
						break;
					}
					case "show":
					{
						jBtn.show();
						break;
					}
				}
				return true;
			}

function TestTimer(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				var jTimerDiv = jCurObjDiv.find(".cl-t-timer");
				var iValue = 0;
				switch(argobj.sAction)
				{
					case "init":
					{
						iValue = CLZ.oStore[argobj.pid].t;
						break;
					}
					case "proceed":
					case "restore":
					{
						iValue = CLZ.oStore[argobj.pid].t - CLZ.oStore[argobj.pid].e;
						if(iValue<0) iValue = 0;
						break;
					}
					case "stop":
					{
						clearTimeout(oCurObj.data.iTestTimeout);
						return false;
					}
					default:
					{
						CLZ.oStore[argobj.pid].e++;
						iValue = CLZ.oStore[argobj.pid].t - CLZ.oStore[argobj.pid].e;
						break;
					}
				}
				if(iValue < 0)
				{
					clearTimeout(oCurObj.data.iTestTimeout);
					oCurObj.FireEvent({ sName: "onTestTimeout" });
					oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { sReason: "timeout", sTarget: "test" } });
					return false;
				}
				var oTime = CLTOOLS.FormatTime({ iValue: iValue });
				var sString = oCurObj.jxParams.children("string_test_time").text();
				var aParts = sString.split("{{1}}");
				sString = aParts.join(oTime.sMin);
				aParts = sString.split("{{2}}");
				sString = aParts.join(oTime.sSec);
				jTimerDiv.find(".cl-t-timer-value").html(sString);
				if(jTimerDiv.find(".cl-t-timer-progress").is(":visible"))
				{
					if(iValue!=CLZ.oStore[argobj.pid].t)
					{
						jTimerDiv.find(".cl-t-timer-progress .rnd-timer-svg-full").hide();
						jTimerDiv.find(".cl-t-timer-progress .rnd-timer-path").show();
						var sPath = "M12,12 L12,0 A12,12";
						if(iValue>=0.5*CLZ.oStore[argobj.pid].t)
						{
							sPath += " 1 1,1 ";
						}
						else
						{
							sPath += " 0 0,1 ";
						}
						var nAngle = 2*Math.PI*(1 - iValue/CLZ.oStore[argobj.pid].t);
						sPath += (12*(1 - Math.sin(nAngle))) + "," + (12*(1 - Math.cos(nAngle))) + " Z";
						jTimerDiv.find(".cl-t-timer-progress .rnd-timer-path").attr({ "d": sPath });
					}
				}
				else
				{
					jTimerDiv.find(".cl-t-timer-scale").css({ "left": (100*(1 - iValue/CLZ.oStore[argobj.pid].t)) + "%" });
				}
				if(iValue<=0)
				{
					clearTimeout(oCurObj.data.iTestTimeout);
					oCurObj.FireEvent({ sName: "onTestTimeout" });
					oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { sReason: "timeout", sTarget: "test" } });
					return false;
				}
				if(argobj.sAction!="restore")
				{
					oCurObj.data.iTestTimeout = setTimeout(function () { oCurObj.CallMethod({ sMethod: "TestTimer" }); }, 1000);
				}
				return true;
			}

function FirstUnfinished(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var iFirstUnfinished = -1;
				for(var i=0; i<CLZ.oStore[argobj.pid].P.length; i++)
				{
					if(iFirstUnfinished==-1)
					{
						if(CLZ.oStore[argobj.pid].Q[i].u!="c")
						{
							iFirstUnfinished = i;
							break;
						}
					}
				}
				return iFirstUnfinished;
			}

function HasAnswer(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var oStore = CLZ.oStore[argobj.pid];
				var oQStore;
				for(var j=0; j<oStore.Q.length; j++)
				{
					if(+oStore.Q[j].i==iIdx)
					{
						oQStore = oStore.Q[j];
						break;
					}
				}
				if(oQStore.A[0].R!=null)
				{
					if(oQStore.A[0].R.length!=0)
					{
						return true;
					}
				}
				return false;
			}

function FirstUnanswered(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var oStore = CLZ.oStore[argobj.pid];
				var iFirstUnanswered = -1;
				for(var j=0; j<oStore.Q.length; j++)
				{
					if(oStore.Q[j].A==null)
					{
						iFirstUnanswered = +oStore.Q[j].i;
						break;
					}
					if(oStore.Q[j].A.length==0)
					{
						iFirstUnanswered = +oStore.Q[j].i;
						break;
					}
					if(oStore.Q[j].A[0].R==null)
					{
						iFirstUnanswered = +oStore.Q[j].i;
						break;
					}
					if(oStore.Q[j].A[0].R.length==0)
					{
						iFirstUnanswered = +oStore.Q[j].i;
						break;
					}
					if(oStore.Q[j].A[0].R[0]=="")
					{
						iFirstUnanswered = +oStore.Q[j].i;
						break;
					}
				}
				return iFirstUnanswered;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem;
				if($(argobj.elem).hasClass("var-spot-td"))
				{
					jElem = $(argobj.elem).parents(".var:first");
				}
				else
				{
					jElem = $(argobj.elem);
				}
				if(jElem.hasClass("var") || jElem.hasClass("var-bullet"))
				{
					if(jElem.hasClass("var-disabled")) return false;
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "mouseout":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.CLStop({ jPlayer: oCurObj.data.jAudioOver });
							}
							break;
						}
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							oCurObj.CallMethod({ sMethod: "HandleSpot", oMethodArgs: { evt: argobj.evt, jElem: jElem } });
							break;
						}
						case "drop":
						{
							argobj.evt.stopImmediatePropagation();
							oCurObj.CallMethod({ sMethod: "HandleOrder", oMethodArgs: { evt: argobj.evt, bullet: argobj.bullet, jElem: jElem } });
							break;
						}
					}
				}
				else if(jElem.hasClass("var-input"))
				{
					oCurObj.CallMethod({ sMethod: "HandleInput", oMethodArgs: { evt: argobj.evt, jElem: jElem } });
				}
				else if(jElem.hasClass("cl-btn"))
				{
					switch(argobj.evt.type)
					{
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							if(jElem.attr("data-action")=="submit")
							{
								oCurObj.CallMethod({ sMethod: "Submit" });
							}
							else if(jElem.attr("data-action")=="next")
							{
								oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { sDir: "next" } });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
							}
							else if(jElem.attr("data-action")=="back")
							{
								//tManager({ pid: argobj.pid, sAction: "back" });
							}
							break;
						}
					}
				}
				else if(jElem.hasClass("q-feedback-btn"))
				{
					switch(argobj.evt.type)
					{
						case "click":
						{
							if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
							}
							oCurObj.CallMethod({ sMethod: "Feedback" });
							break;
						}
					}
				}
				else if(jElem.hasClass("cl-t-progress-item-btn"))
				{
					if(CLZ.bSoundOn && oCurObj.data.sSoundClick!="none")
					{
						CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioClick });
					}
					oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { qid: +jElem.parent().attr("data-q-num") } });
				}
			}

function HandleSpot(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = argobj.jElem;
				var sQType = jElem.attr("data-type");
				var iQId = +jElem.attr("data-qid");
				oCurObj.CallMethod({ sMethod: "Feedback" });
				var oQStore = null;
				for(var i=0; i<CLZ.oStore[argobj.pid].Q.length; i++)
				{
					if(+CLZ.oStore[argobj.pid].Q[i].i===iQId)
					{
						oQStore = CLZ.oStore[argobj.pid].Q[i];
						break;
					}
				}
				if(oQStore==null) return false;
				if(oQStore.u!="c")
				{
					var oAttempt = null;
					for(var i=0; i<oQStore.A.length; i++)
					{
						if(oQStore.A[i].u!="c")
						{
							oAttempt = oQStore.A[i];
							break;
						}
					}
					if(oAttempt==null) return false;
				}
				switch(sQType)
				{
					case "true-false":
					{
						if(jElem.hasClass("var-selected")) return false;
						var jParent = jElem.parents("div.q-vars-true-false:first");
						jParent.find(".var-selected").removeClass("var-selected").addClass("var-idle");
						jElem.removeClass("var-idle").addClass("var-selected");
						jParent.attr({ "data-selection": jElem.attr("data-vid"), "data-touched": "1" });
						if(oQStore.u!="c")
						{
							oAttempt.R[0] = +jElem.attr("data-vid");
							oQStore.R[0] = +jElem.attr("data-vid");
							oAttempt.u = "i";
							oQStore.u = "i";
							oCurObj.FireEvent({ sName: "onChangeChoice" });
						}
						break;
					}
					case "choice":
					{
						if(jElem.hasClass("var-selected")) return false;
						jElem.siblings(".var").removeClass("var-selected").addClass("var-idle");
						jElem.removeClass("var-idle").addClass("var-selected");
						jElem.parent().attr({ "data-selection": jElem.attr("data-vid"), "data-touched": "1" });
						if(oQStore.u!="c")
						{
							oAttempt.R[0] = +jElem.attr("data-vid");
							oQStore.R[0] = +jElem.attr("data-vid");
							oAttempt.u = "i";
							oQStore.u = "i";
							oCurObj.FireEvent({ sName: "onChangeChoice" });
						}
						break;
					}
					case "select":
					{
						if(jElem.hasClass("var-selected"))
						{
							jElem.removeClass("var-selected").addClass("var-idle");
						}
						else
						{
							jElem.removeClass("var-idle").addClass("var-selected");
						}
						jElem.parent().attr({ "data-touched": "1" });
						if(oQStore.u!="c")
						{
							oAttempt.R = [];
							oQStore.R = [];
							jElem.parent().children(".var").each( function()
							{
								if($(this).hasClass("var-selected"))
								{
									oAttempt.R.push(+$(this).attr("data-vid"));
									oQStore.R.push(+$(this).attr("data-vid"));
								}
							});
							oAttempt.u = "i";
							oQStore.u = "i";
							oCurObj.FireEvent({ sName: "onChangeSelect" });
						}
						break;
					}
				}
				if(oQStore.u!="c")
				{
					oCurObj.CallMethod({ sMethod: "UpdateRail" });
					if(oCurObj.data.sDisplayAs=="page")
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: ((oQStore.R.length>0) ? "show" : "hide") } });
					}
					else
					{
						if(oCurObj.jxParams.children("allow_partial_answer").text()=="yes")
						{
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
						}
						else
						{
							if(oCurObj.CallMethod({ sMethod: "FirstUnanswered" })==-1)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							}
						}
					}
				}
				return true;
			}

function HandleOrder(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = argobj.jElem;
				var sQType = jElem.attr("data-type");
				var iQId = +jElem.attr("data-qid");
				oCurObj.CallMethod({ sMethod: "Feedback" });
				var oQStore = null;
				for(var i=0; i<CLZ.oStore[argobj.pid].Q.length; i++)
				{
					if(+CLZ.oStore[argobj.pid].Q[i].i===iQId)
					{
						oQStore = CLZ.oStore[argobj.pid].Q[i];
						break;
					}
				}
				if(oQStore==null) return false;
				if(oQStore.u!="c")
				{
					var oAttempt = null;
					for(var i=0; i<oQStore.A.length; i++)
					{
						if(oQStore.A[i].u!="c")
						{
							oAttempt = oQStore.A[i];
							break;
						}
					}
					if(oAttempt==null) return false;
				}
				var iTargetIdx = +jElem.attr("data-vid");
				var iBulletIdx = +$(argobj.bullet).attr("data-vid");
				var aNewOrder = [];
				var aOldOrder = jElem.parent().attr("data-order").split("|");
				for(var i=0; i<aOldOrder.length; i++)
				{
					if(+aOldOrder[i]==+iTargetIdx)
					{
						aNewOrder.push(+iBulletIdx);
					}
					else if(+aOldOrder[i]==+iBulletIdx)
					{
						aNewOrder.push(+iTargetIdx);
					}
					else
					{
						aNewOrder.push(+aOldOrder[i]);
					}
				}
				if(jElem.hasClass("var-bullet"))
				{
					var aTops = jElem.attr("data-tops").split("|");
					var jParent = jElem.parent();
					for(var i=0; i<aNewOrder.length; i++)
					{
						jParent.children(".var-bullet[data-vid='"+ aNewOrder[i] +"']").css({ "top": aTops[i] + "px" });
					}
					jElem.parent().attr({ "data-order": aNewOrder.join("|"), "data-touched": "1" });
					oCurObj.FireEvent({ sName: "onChangeMatch" });
				}
				else
				{
					var jQVars = jElem.parent(".q-vars:first");
					var jVars = jQVars.find(".var-order-elem");
					jVars.each(function ()
					{
						$(this).css({ "position": "relative", "left": "", "top": "" });
					});
					var aOrder = [];
					for(var i=0; i<aNewOrder.length; i++)
					{
						jQVars.find(".var-order-elem[data-vid='" + aNewOrder[i] + "']").appendTo(jQVars);
						aOrder.push(aNewOrder[i]);
					}
					jQVars.attr({ "data-order": aNewOrder.join("|"), "data-touched": "1" });
					oCurObj.FireEvent({ sName: "onChangeOrder" });
				}
				if(oQStore.u!="c")
				{
					oAttempt.R = aNewOrder.slice(0);
					oAttempt.u = "i";
					oQStore.R = aNewOrder.slice(0);
					oQStore.u = "i";
					oCurObj.CallMethod({ sMethod: "UpdateRail" });
					if(oCurObj.data.sDisplayAs=="page")
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } })
					}
					else
					{
						if(oCurObj.jxParams.children("allow_partial_answer").text()=="yes")
						{
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
						}
						else
						{
							if(oCurObj.CallMethod({ sMethod: "FirstUnanswered" })==-1)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							}
						}
					}
				}
				return true;
			}

function HandleInput(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = argobj.jElem;
				var sQType = jElem.attr("data-type");
				var jQVars = jElem.parents(".q-vars:first");
				var iQId = +jQVars.attr("data-qid");
				oCurObj.CallMethod({ sMethod: "Feedback" });
				var oQStore = null;
				var oQuestion;
				for(var i=0; i<CLZ.oStore[argobj.pid].Q.length; i++)
				{
					if(+CLZ.oStore[argobj.pid].Q[i].i===iQId)
					{
						oQStore = CLZ.oStore[argobj.pid].Q[i];
						break;
					}
				}
				if(oQStore==null) return false;
				oQuestion = oCurObj.data.aQuestions[+oQStore.i];
				if(oQStore.u!="c")
				{
					var oAttempt = null;
					for(var i=0; i<oQStore.A.length; i++)
					{
						if(oQStore.A[i].u!="c")
						{
							oAttempt = oQStore.A[i];
							break;
						}
					}
					if(oAttempt==null) return false;
				}
				switch(argobj.evt.type)
				{
					case "blur":
					{
						break;
					}
					case "keyup":
					{
						if(oQuestion.sType=="numeric")
						{
							jElem.val( CL.Q.FixNumeric({ sText: jElem.val() }) );
						}
						jQVars.attr({"data-touched": "1"});
						break;
					}
				}
				if(oQStore.u!="c")
				{
					oQStore.R = [];
					oAttempt.R = [];
					jQVars.find(".var[data-vid]").each(function ()
					{
						var sVal = $(this).find(".var-input").val();
						oQStore.R.push(sVal);
						oAttempt.R.push(sVal);
					});
					oCurObj.CallMethod({ sMethod: "UpdateRail" });
					if(oCurObj.data.sDisplayAs=="page")
					{
						oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } })
					}
					else
					{
						if(oCurObj.jxParams.children("allow_partial_answer").text()=="yes")
						{
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
						}
						else
						{
							if(oCurObj.CallMethod({ sMethod: "FirstUnanswered" })==-1)
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "show" } });
							}
							else
							{
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							}
						}
					}
					oCurObj.FireEvent({ sName: ((sQType=="numeric") ? "onChangeNumericValue" : "onChangeTextValue") });
				}
				return true;
			}

function EvalQuestion(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(argobj.oQStore==null) return false;
				var jxQuestion = $(oCurObj.data.jxQuestions[+argobj.qid]);
				var oQuestion = oCurObj.data.aQuestions[+argobj.qid];
				var sQType = oQuestion.sType;
				var jQuestion = jCurObjDiv.find(".q-question[data-q-num='" + argobj.qid + "']");
				var jQVars = jQuestion.find(".q-vars:first");
				var bCorrect = false;
				var sSelected;
				var sCorrect;
				var jxVars;
				var aSelection;
				switch(sQType)
				{
					case "true-false":
					{
						if(argobj.oQStore.R.length!=0)
						{
							bCorrect = (+argobj.oQStore.R[0]==+argobj.oQStore.P[0]);
						}
						return bCorrect;
					}
					case "choice":
					{
						if(argobj.oQStore.R.length==0) return false;
						return (+argobj.oQStore.P[0]==+argobj.oQStore.R[0]);
					}
					case "select":
					{
						if(argobj.oQStore.P.length!=argobj.oQStore.R.length)
						{
							return false;
						}
						bCorrect = true;
						for(var i=1; i<=oQuestion.jxVars.length; i++)
						{
							if($.inArray(i, argobj.oQStore.P)!=-1)
							{
								if($.inArray(i, argobj.oQStore.R)!=-1)
								{
									continue;
								}
								else
								{
									bCorrect = false;
									break;
								}
							}
							else
							{
								if($.inArray(i, argobj.oQStore.R)!=-1)
								{
									bCorrect = false;
									break;
								}
								else
								{
									continue;
								}
							}
						}
						return bCorrect;
					}
					case "order":
					case "oto":
					{
						for(var i=0; i<oQuestion.jxVars.length; i++)
						{
							if(argobj.oQStore.P[i]!=argobj.oQStore.R[i])
							{
								return false;
							}
						}
						return true;
					}
					case "numeric":
					case "fib":
					{
						var aCorrect = [];
						for(var i=0; i<argobj.oQStore.P.length; i++)
						{
							aCorrect.push(CL.Q.EvalSet({ sType: sQType, sValue: argobj.oQStore.R[i], oPattern: argobj.oQStore.P[i] }));
						}
						var sOp = jxQuestion.find("q_var_logic_" + sQType).text();
						bCorrect = (sOp=="AND");
						for(var i=0; i<aCorrect.length; i++)
						{
							if(sOp=="AND")
							{
								if(aCorrect[i]!=true)
								{
									bCorrect = false;
									break;
								}
							}
							else if(sOp=="OR")
							{
								if(aCorrect[i]==true)
								{
									bCorrect = true;
									break;
								}
							}
						}
						return bCorrect;
					}
				}
				return bCorrect;
			}

function Submit(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var iIdx;
				var sQType;
				var sIntId;
				var jQVars;
				oCurObj.FireEvent({ sName: "onAttempt" });
				var oStore = CLZ.oStore[argobj.pid];
				switch(oCurObj.data.sDisplayAs)
				{
					case "list":
					{
						var bStopTest = (argobj.sReason=="timeout");
						if(!bStopTest)
						{
							var iFirstUnanswered = oCurObj.CallMethod({ sMethod: "FirstUnanswered" });
							if(iFirstUnanswered!=-1)
							{
								if(oCurObj.jxParams.children("allow_partial_answer").text()=="yes")
								{
									if(confirm(oCurObj.jxParams.children("t_proceed_confirm").text()))
									{
										oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { qid: iFirstUnanswered } });
										return false;
									}
									else
									{
										bStopTest = true;
									}
								}
								else
								{
									oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { qid: iFirstUnanswered } });
									return false;
								}
							}
							else
							{
								bStopTest = true;
							}
						}
						if(bStopTest)
						{
							// check all and stop test
							var sIntId;
							var iIdx;
							var sResult;
							var nFinalScore;
							var bCorrect;
							var jxObj;
							var sScoringType;
							var jxQuestion;
							var sQType;
							var jQVars;
							var oQStore;
							var oQuestion;
							var jxQuestion;
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							for(var i=0; i<oStore.P.length; i++)
							{
								iIdx = oStore.P[i];
								for(var j=0; j<oStore.Q.length; j++)
								{
									if(+oStore.Q[j].i==iIdx)
									{
										oQStore = oStore.Q[j];
										break;
									}
								}
								oQuestion = oCurObj.data.aQuestions[iIdx];
								jxQuestion = $(oCurObj.data.jxQuestions[iIdx]);
								if(oQStore.R.length==0)
								{
									bCorrect = false;
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { oQStore: oQStore, bCorrect: bCorrect, bInitNext: false } });
								}
								else
								{
									bCorrect = oCurObj.CallMethod({ sMethod: "EvalQuestion", oMethodArgs: { qid: iIdx, oQStore: oQStore } });
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { oQStore: oQStore, bCorrect: bCorrect, bInitNext: false } });
									var nFinalScore = 0;
									if(bCorrect)
									{
										if(oCurObj.data.bCredit)
										{
											switch(oQuestion.sScoringType)
											{
												case "fixed": // fixed - 2.7 legacy
												case "plain":
												{
													nScore = +jxQuestion.children("q_weight").text();
													for(var k=0; k<oQuestion.aObjectives.length; k++)
													{
														CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
													}
													nFinalScore = nScore;
													break;
												}
												case "regressive":
												{
													var nWeight = +jxQuestion.children("q_weight").text();
													var iCompleted = 0;
													for(var k=0; k<oQStore.A.length; k++)
													{
														if(oQStore.A[k].u=="c")
														{
															iCompleted++;
														}
													}
													nScore = nWeight*(oQStore.A.length + 1 - iCompleted);
													for(var k=0; k<oQuestion.aObjectives.length; k++)
													{
														CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
													}
													nFinalScore = nScore;
													break;
												}
												case "byobj":
												{
													for(var k=0; k<oQuestion.aObjectives.length; k++)
													{
														nScore = oQuestion.aObjectives[k].nWeight;
														CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
									}
									else
									{
										if(oCurObj.data.bCredit)
										{
											nScore = 0;
											for(var k=0; k<oQuestion.aObjectives.length; k++)
											{
												CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + oQStore.i, bReplace: true, nScore: nScore });
											}
										}
									}
								}
								oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: {  oQStore: oQStore  }});
							}
							oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "stop" } });
							oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: CLZ.oStore[argobj.pid].P } });
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
							jCurObjDiv.find(".cl-t-footer-cell-center").html((argobj.sReason=="timeout") ? oCurObj.jxParams.find("t_stop_msg").text() : oCurObj.jxParams.find("t_allanswered_msg").text());
							jCurObjDiv.find(".cl-t-footer-cell-right").html("&nbsp;");
							CLZ.oStore[argobj.pid].u = (argobj.sReason=="timeout") ? "t" : "c";
							CLZ.oStore[argobj.pid].d = (new Date()).valueOf() - CLZ.oStore[argobj.pid].s;
							oCurObj.FireEvent({ sName: "onTestEnd" });
						}
						break;
					}
					case "page":
					{
						var bStopTest = (argobj.sReason=="timeout" && argobj.sTarget=="test");
						if(bStopTest)
						{
							// testtimeout - check all and stop test
							var sIntId;
							var iIdx;
							var sResult;
							var nFinalScore;
							var bCorrect;
							var jxObj;
							var sScoringType;
							var jxQuestion;
							var sQType;
							var jQVars;
							var oQStore;
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							if(oCurObj.data.iQTimeout) clearTimeout(oCurObj.data.iQTimeout);
							for(var i=0; i<oStore.P.length; i++)
							{
								iIdx = oStore.P[i];
								for(var j=0; j<oStore.Q.length; j++)
								{
									if(+oStore.Q[j].i==iIdx)
									{
										oQStore = oStore.Q[j];
										break;
									}
								}
								oQuestion = oCurObj.data.aQuestions[+oQStore.i];
								if(oQStore.u=="c") continue;

								if(oQStore.R.length==0)
								{
									bCorrect = false;
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { oQStore: oQStore, bCorrect: bCorrect, bInitNext: false } });
								}
								else
								{
									bCorrect = oCurObj.CallMethod({ sMethod: "EvalQuestion", oMethodArgs: { qid: iIdx, oQStore: oQStore } });
									oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { oQStore: oQStore, bCorrect: bCorrect, bInitNext: false } });
									var nFinalScore = 0;
									if(bCorrect)
									{
										if(oCurObj.data.bCredit)
										{
											switch(oQuestion.sScoringType)
											{
												case "fixed": // fixed - 2.7 legacy
												case "plain":
												{
													nScore = +jxQuestion.children("q_weight").text();
													for(var k=0; k<oQuestion.aObjectives.length; k++)
													{
														CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
													}
													nFinalScore = nScore;
													break;
												}
												case "regressive":
												{
													var nWeight = +jxQuestion.children("q_weight").text();
													var iCompleted = 0;
													for(var k=0; k<oQStore.A.length; k++)
													{
														if(oQStore.A[k].u=="c")
														{
															iCompleted++;
														}
													}
													nScore = nWeight*(oQStore.A.length + 1 - iCompleted);
													for(var k=0; k<oQuestion.aObjectives.length; k++)
													{
														CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
													}
													nFinalScore = nScore;
													break;
												}
												case "byobj":
												{
													for(var k=0; k<oQuestion.aObjectives.length; k++)
													{
														nScore = oQuestion.aObjectives[k].nWeight;
														CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
													}
													break;
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: [ iIdx ] } });
										oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: {  oQStore: oQStore  }});
									}
									else
									{
										if(oCurObj.data.bCredit)
										{
											nScore = 0;
											for(var k=0; k<oQuestion.aObjectives.length; k++)
											{
												CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[k].sId, sSrcId: argobj.pid + "_" + oQStore.i, bReplace: true, nScore: nScore });
											}
										}
										oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: [ iIdx ] } });
										oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: {  oQStore: oQStore  } });
									}
								}
							}
							oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "stop" } });
							oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: CLZ.oStore[argobj.pid].P } });
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
							oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
							jCurObjDiv.find(".cl-t-footer-cell-center").html((argobj.sReason=="timeout") ? oCurObj.jxParams.find("t_stop_msg").text() : oCurObj.jxParams.find("t_allanswered_msg").text());
							jCurObjDiv.find(".cl-t-footer-cell-right").html("&nbsp;");
							CLZ.oStore[argobj.pid].u = "c";
							oCurObj.FireEvent({ sName: "onTestEnd" });
						}
						else
						{
							var jQuestion = jCurObjDiv.find(".q-question:visible");
							var jQVars = jQuestion.find(".q-vars:first");
							var iIdx = +jQuestion.attr("data-q-num");
							var oQStore = null;
							for(var i=0; i<CLZ.oStore[argobj.pid].Q.length; i++)
							{
								if(+CLZ.oStore[argobj.pid].Q[i].i===iIdx)
								{
									oQStore = CLZ.oStore[argobj.pid].Q[i];
									break;
								}
							}
							if(oQStore==null) return false;
							var jxQuestion = $(oCurObj.data.jxQuestions[iIdx]);
							var oQuestion = oCurObj.data.aQuestions[iIdx];
							var nScore = 0;
							var bCorrect = false;
							if(oQStore.R.length==0)
							{
								bCorrect = false;
								oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { oQStore: oQStore, bCorrect: bCorrect, bInitNext: (argobj.sReason=="timeout" ? false: !bCorrect) } });
								if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
								{
									CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
								}
								oCurObj.FireEvent({ sName: "onFailure" });
							}
							else
							{
								bCorrect = oCurObj.CallMethod({ sMethod: "EvalQuestion", oMethodArgs: { qid: iIdx, oQStore: oQStore } });
								oCurObj.CallMethod({ sMethod: "CloseAttempt", oMethodArgs: { oQStore: oQStore, bCorrect: bCorrect, bInitNext: (argobj.sReason=="timeout" ? false: !bCorrect) } });
								var nFinalScore = 0;
								if(bCorrect)
								{
									if(CLZ.bSoundOn && oCurObj.data.sSoundSuccess!="none")
									{
										CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioSuccess });
									}
									oCurObj.FireEvent({ sName: "onSuccess" });
									if(oCurObj.data.bCredit)
									{
										switch(oQuestion.sScoringType)
										{
											case "fixed": // fixed - 2.7 legacy
											case "plain":
											{
												nScore = +jxQuestion.children("q_weight").text();
												for(var i=0; i<oQuestion.aObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[i].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
												}
												nFinalScore = nScore;
												break;
											}
											case "regressive":
											{
												var nWeight = +jxQuestion.children("q_weight").text();
												var iCompleted = 0;
												for(var k=0; k<oQStore.A.length; k++)
												{
													if(oQStore.A[k].u=="c")
													{
														iCompleted++;
													}
												}
												nScore = nWeight*(oQStore.A.length + 1 - iCompleted);
												for(var i=0; i<oQuestion.aObjectives.length; i++)
												{
													CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[i].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
												}
												nFinalScore = nScore;
												break;
											}
											case "byobj":
											{
												for(var i=0; i<oQuestion.aObjectives.length; i++)
												{
													nScore = oQuestion.aObjectives[i].nWeight;
													CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[i].sId, sSrcId: argobj.pid + "_" + iIdx, bReplace: true, nScore: nScore });
												}
												break;
											}
										}
									}
									if(oCurObj.data.iQTimeout) clearTimeout(oCurObj.data.iQTimeout);
									oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
									oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: [ iIdx ] } });
									oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: {  oQStore: oQStore  }});
									oCurObj.FireEvent({ sName: "onQuestionEnd" });
									var iFirstUnfinished = oCurObj.CallMethod({ sMethod: "FirstUnfinished" });
									if(oCurObj.data.bCombineBtn)
									{
										if(iFirstUnfinished==-1)
										{
											bStopTest = true;
										}
										else
										{
											oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { sDir: "next" } });
										}
									}
									else
									{
										if(oCurObj.data.bAllowFeedback)
										{
											oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { sType: "correct", xQuestion: jxQuestion } });
										}
										if(iFirstUnfinished==-1)
										{
											bStopTest = true;
										}
										else
										{
											oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "show" } });
										}
									}
								}
								else
								{
									if(CLZ.bSoundOn && oCurObj.data.sSoundFailure!="none")
									{
										CL.Sound.CLPlay({ jPlayer: oCurObj.data.jAudioFailure });
									}
									oCurObj.FireEvent({ sName: "onFailure" });
									if(oQuestion.bNoMoreAttempts==true || argobj.sReason=="timeout")
									{
										if(oCurObj.data.bCredit)
										{
											nScore = 0;
											for(var i=0; i<oQuestion.aObjectives.length; i++)
											{
												CL.SCO.SetObjectiveScore({ sId: oQuestion.aObjectives[i].sId, sSrcId: argobj.pid + "_" + oQStore.i, bReplace: true, nScore: nScore });
											}
										}
										if(oCurObj.data.iQTimeout) clearTimeout(oCurObj.data.iQTimeout);
										oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
										oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: [ iIdx ] } });
										oCurObj.CallMethod({ sMethod: "CloseQuestion", oMethodArgs: {  oQStore: oQStore  } });
										var iFirstUnfinished = oCurObj.CallMethod({ sMethod: "FirstUnfinished" });
										if(oCurObj.data.bCombineBtn)
										{
											if(iFirstUnfinished==-1)
											{
												bStopTest = true;
											}
											else
											{
												oCurObj.CallMethod({ sMethod: "GoTo", oMethodArgs: { sDir: "next" } });
											}
										}
										else
										{
											if(oCurObj.data.bShowCorrect)
											{
												oCurObj.CallMethod({ sMethod: "RestoreQuestionState", oMethodArgs: { qid: iIdx, bCorrect: true } });
											}
											if(oCurObj.data.bAllowFeedback)
											{
												if(argobj.sQReason=="timeout")
												{
													oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { sType: "timeout", xQuestion: jxQuestion } });
												}
												else
												{
													oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { sType: "exceed", xQuestion: jxQuestion } });
												}
											}
											if(iFirstUnfinished==-1)
											{
												bStopTest = true;
											}
											else
											{
												oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "show" } });
											}
										}
									}
									else
									{
										if(oCurObj.data.bAllowFeedback)
										{
											if(argobj.sQReason=="timeout")
											{
												oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { sType: "timeout", xQuestion: jxQuestion } });
											}
											else
											{
												if(oQuestion.iAttemptsLeft==1)
												{
													oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { sType: "lastattempt", xQuestion: jxQuestion } });
												}
												else
												{
														oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { sType: "incorrect", xQuestion: jxQuestion } });
												}
											}
										}
										oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
									}
								}
							}
							if(bStopTest)
							{
								oCurObj.CallMethod({ sMethod: "TestTimer", oMethodArgs: { sAction: "stop" } });
								oCurObj.CallMethod({ sMethod: "DisableQuestions", oMethodArgs: { aTargets: CLZ.oStore[argobj.pid].P } });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "submit", sAction: "hide" } });
								oCurObj.CallMethod({ sMethod: "Button", oMethodArgs: { sBtn: "next", sAction: "hide" } });
								jCurObjDiv.find(".cl-t-footer-cell-center").html((argobj.sReason=="timeout") ? oCurObj.jxParams.find("t_stop_msg").text() : oCurObj.jxParams.find("t_allanswered_msg").text());
								jCurObjDiv.find(".cl-t-footer-cell-right").html("&nbsp;");
								CLZ.oStore[argobj.pid].u = (argobj.sReason=="timeout") ? "t" : "c";
								oCurObj.FireEvent({ sName: "onTestEnd" });
							}
						}
						break;
					}
				}
				return true;
			}

function CloseAttempt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(argobj.oQStore==null) return false;
				var iAttemptIdx = -1;
				var oAttempt = null;
				for(var i=0; i<argobj.oQStore.A.length; i++)
				{
					if(argobj.oQStore.A[i].u=="c") continue;
					iAttemptIdx = i;
					oAttempt = argobj.oQStore.A[i];
					break;
				}
				if(oAttempt==null) return false;
				var iStart = CLZ.oStore[argobj.pid].s + argobj.oQStore.s + oAttempt.s;
				var iNow = (new Date()).valueOf();
				oAttempt.d = iNow - iStart;
				oAttempt.u = "c"; // current attempt closed
				iAttemptIdx++;
				var iAttemptsLeft = argobj.oQStore.A.length - iAttemptIdx;
				if(iAttemptsLeft<0)
				{
					iAttemptsLeft = 0;
				}
				if(argobj.bCorrect)
				{
					argobj.oQStore.r = argobj.bCorrect ? "c" : "i";
				}
				if(!argobj.bInitNext)
				{
					return true;
				}
				oCurObj.data.aQuestions[+argobj.oQStore.i].iAttemptsLeft = iAttemptsLeft;
				if(iAttemptsLeft==0 || argobj.oQStore.A[iAttemptIdx]==null)
				{
					oCurObj.data.aQuestions[+argobj.oQStore.i].bNoMoreAttempts = true;
					oCurObj.FireEvent({ sName: "onAttemptsLimit" });
				}
				else
				{
					if(argobj.bInitNext)
					{
						var sString = oCurObj.jxParams.children("string_question_attempts").text();
						var aParts = sString.split("{{2}}");
						sString = aParts.join(argobj.oQStore.A.length);
						aParts = sString.split("{{1}}");
						sString = aParts.join((iAttemptIdx + 1));
						jCurObjDiv.find(".cl-t-attempts").html(sString);
						argobj.oQStore.A[iAttemptIdx] =
						{
							s: iNow - argobj.oQStore.s - CLZ.oStore[argobj.pid].s,
							d: 0,
							u: "i",
							R: []
						}
						if((argobj.oQStore.A.length>1) && (iAttemptIdx==argobj.oQStore.A.length-1))
						{
							oCurObj.data.aQuestions[+argobj.oQStore.i].bLastAttempt = true;
							oCurObj.FireEvent({ sName: "onLastAttemptStart" });
						}
					}
				}
				return true;
			}

function CloseQuestion(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(argobj.oQStore==null) return false;
				oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { action: "stop" } });
				var jQuestion = jCurObjDiv.find(".q-question[data-q-num='" + argobj.oQStore.i + "']");
				var oQuestion = oCurObj.data.aQuestions[+argobj.oQStore.i];
				var jQVars = jQuestion.find(".q-vars:first");
				var jVars;
				switch(oQuestion.sType)
				{
					case "true-false":
					case "choice":
					case "select":
					case "order":
					{
						jVars = jQVars.find(".cl-var");
						break;
					}
					case "numeric":
					case "fib":
					{
						jVars = jQVars.find(".cl-var");
						jVars.find(".cl-var-input").prop("disabled", true);
						break;
					}
					case "oto":
					case "otm":
					case "mtm":
					{
						jVars = jQVars.find(".cl-var-bullet");
						if(jVars.hasClass("ui-draggable"))
						{
							jVars.draggable("destroy");
						}
						if(jVars.hasClass("ui-droppable"))
						{
							jVars.droppable("destroy");
						}
						break;
					}
				}
				jVars.addClass("cl-var-disabled");
				argobj.oQStore.d = (new Date()).valueOf() - argobj.oQStore.s - CLZ.oStore[argobj.pid].s;
				argobj.oQStore.u = "c";
				if($.inArray(+argobj.oQStore.i, CLZ.oStore[argobj.pid].R)==-1)
				{
					CLZ.oStore[argobj.pid].R.push(+argobj.oQStore.i);
				}
				oCurObj.FireEvent({ sName: "onQuestionEnd" })
				return true;
			}

function Timer(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				var jCurObjDiv = $(oCurObj.div);

				var jTimerDiv = jCurObjDiv.find(".cl-q-timer");

				var iValue = 0;
				var oQStore = CLZ.oStore[argobj.pid].Q[+argobj.qid];
				switch(argobj.sAction)
				{
					case "init":
					{
						jCurObjDiv.find(".cl-q-timer-container").show();
						jCurObjDiv.find(".cl-q-no-timer").hide();
						iValue = oQStore.t;
						break;
					}
					case "hide":
					{
						jCurObjDiv.find(".cl-q-timer-container").hide();
						jCurObjDiv.find(".cl-q-no-timer").show();
						return true;
					}
					case "proceed":
					case "restore":
					{
						jCurObjDiv.find(".cl-q-timer-container").show();
						jCurObjDiv.find(".cl-q-no-timer").hide();
						iValue = oQStore.t - oQStore.e;
						if(iValue<0) iValue = 0;
						break;
					}
					case "stop":
					{
						clearTimeout(oCurObj.data.iQTimeout);
						return false;
					}
					default:
					{
						oQStore.e++;
						iValue = oQStore.t - oQStore.e;
						break;
					}
				}
				if(iValue < 0)
				{
					clearTimeout(oCurObj.data.iQTimeout);
					oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { qid: argobj.qid, sReason: "timeout", sTarget: "test" } });
					oCurObj.FireEvent({ sName: "onTimeout" });
					return false;
				}
				var oTime = CLTOOLS.FormatTime({ iValue: iValue });
				var sString = oCurObj.jxParams.children("string_question_time").text();
				var aParts = sString.split("{{1}}");
				sString = aParts.join(oTime.sMin);
				aParts = sString.split("{{2}}");
				sString = aParts.join(oTime.sSec);
				jTimerDiv.find(".cl-q-timer-value").html(sString);
				if(jTimerDiv.find(".cl-q-timer-progress").is(":visible"))
				{
					if(iValue!=oQStore.t)
					{
						jTimerDiv.find(".cl-q-timer-progress .rnd-timer-svg-full").hide();
						jTimerDiv.find(".cl-q-timer-progress .rnd-timer-path").show();
						var sPath = "M12,12 L12,0 A12,12";
						if(iValue>=0.5*oQStore.t)
						{
							sPath += " 1 1,1 ";
						}
						else
						{
							sPath += " 0 0,1 ";
						}
						var nAngle = 2*Math.PI*(1 - iValue/oQStore.t);
						sPath += (12*(1 - Math.sin(nAngle))) + "," + (12*(1 - Math.cos(nAngle))) + " Z";
						jTimerDiv.find(".cl-q-timer-progress .rnd-timer-path").attr({ "d": sPath });
					}
				}
				else
				{
					jTimerDiv.find(".cl-q-timer-scale").css({ "left": (100*(1 - iValue/oQStore.t)) + "%" });
				}
				if(argobj.sAction!="restore")
				{
					if(iValue <= 0)
					{
						clearTimeout(oCurObj.data.iQTimeout);
						oCurObj.CallMethod({ sMethod: "Submit", oMethodArgs: { qid: argobj.qid, sReason: "timeout" } });
						oCurObj.FireEvent({ sName: "onTimeout" });
						return false;
					}
					oCurObj.data.iQTimeout = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Timer", oMethodArgs: { qid: argobj.qid } }); }, 1000);
				}
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				var iIdx = (oCurObj.data.sDisplayAs == "list") ? -1 : +jCurObjDiv.find(".q-question:visible").attr("data-q-num");
				switch(argobj.property.toLowerCase())
				{
					case "totalquestionquantity":    	return CLZ.oStore[argobj.pid].P.length;
					case "testmode":                	return oCurObj.data.sMode;
					case "testduration":            	return CLZ.oStore[argobj.pid].t;
					case "testtimer":                	return CLZ.oStore[argobj.pid].e;
					case "questionnumber":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						return ($.inArray(iIdx, CLZ.oStore[argobj.pid].P) + 1);
					}
					case "questiontype":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return "";
						}
						return jCurObjDiv.find(".q-question:visible").attr("data-type");
					}
					case "questionduration":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						var iIdx = +jCurObjDiv.find(".q-question:visible").attr("data-q-num");
						return -1;
					}
					case "questiontimer":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						if(oCurObj.data.aQuestions[iIdx].bTimer)
						{
							var i = $.inArray(iIdx, CLZ.oStore[argobj.pid].P);
							if(i==-1) return -1;
							return CLZ.oStore[argobj.pid].Q[i].e;
						}
						else
						{
							return -1;
						}
					}
					case "itemquantity":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						return jCurObjDiv.find(".q-question:visible .var").length;
					}
					case "questionvalue":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						var i = $.inArray(iIdx, CLZ.oStore[argobj.pid].P);
						if(i==-1) return -1;
						return CLZ.oStore[argobj.pid].Q[i].R.join("~");
					}
					case "attemptslast":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						var i = $.inArray(iIdx, CLZ.oStore[argobj.pid].P);
						if(i==-1) return -1;
						for(var j=CLZ.oStore[argobj.pid].Q[i].A.length; j>=0; j--)
						{
							if(CLZ.oStore[argobj.pid].Q[i].A[j].u!="c")
							{
								return i;
							}
						}
						return 0;
					}
					case "attemptstotal":
					{
						if(oCurObj.data.sDisplayAs == "list")
						{
							return -1;
						}
						var i = $.inArray(iIdx, CLZ.oStore[argobj.pid].P);
						if(i==-1) return -1;
						return CLZ.oStore[argobj.pid].Q[i].A.length;
					}
				}
				return "";
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false;
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Init" });
						break;
					}
				}
				return true;
			}

function Feedback(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(!oCurObj.data.bAllowFeedback)
				{
					return false;
				}
				var jCurState = jCurObjDiv.find(".cl-state[data-state='default']");
				jCurState.find(".q-feedback").remove();
				if(oCurObj.iFBTimer!=null)
				{
					clearTimeout(oCurObj.iFBTimer);
				}
				if(argobj.sType==null)
				{
					return true;
				}
				var sFBText = "";
				var sType = argobj.sType;
				if(oCurObj.data.bAllowFeedback)
				{
					var jxQuestion = $(argobj.xQuestion);
					if(jxQuestion.children("q_fb_test").text()!="yes")
					{
						if((sType=="exceed" && jxQuestion.children("display_fb_exceed").text()!="yes") || (sType=="lastattempt" && jxQuestion.children("display_fb_lastattempt").text()!="yes"))
						{
							sType = "incorrect";
						}
						if(jxQuestion.children("display_fb_" + sType).text()=="yes")
						{
							sFBText = jxQuestion.children("fb_" + sType).text();
						}
					}
					else
					{
						if((sType=="exceed" && oCurObj.jxParams.children("q_display_fb_exceed").text()!="yes") || (sType=="lastattempt" && oCurObj.jxParams.children("q_display_fb_lastattempt").text()!="yes"))
						{
							sType = "incorrect";
						}
						if(oCurObj.jxParams.children("q_display_fb_" + sType).text()=="yes")
						{
							sFBText = oCurObj.jxParams.children("q_fb_" + sType).text();
						}
					}
				}
				if(sFBText!="")
				{
					var jFB = jCurObjDiv.find(".q-feedback-template").clone(true).removeClass("q-feedback-template").addClass("q-feedback q-feedback-" + sType).appendTo(jCurState);
					jFB.find(".text-main").html(sFBText).fadeIn();
					if(oCurObj.jxParams.children("q_fb_timerswitch").text()=="yes")
					{
						oCurObj.iFBTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Feedback", oMethodArgs: { hide: true } }); }, +oCurObj.jxParams.children("q_fb_timer").text());
					}
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false;
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}

				oCurObj.data =
				{
					bDisplayAttempts: (oCurObj.jxParams.children("display_attempts").text()=="yes"),
					bDisplayCorrects: (oCurObj.jxParams.children("display_corrects").text()=="yes"),
					bDisplayMainScore: (oCurObj.jxParams.children("display_main_score").text()=="yes"),
					bDisplayQuestion: (oCurObj.jxParams.children("display_questiontext").text()=="yes"),
					bDisplayScores: (oCurObj.jxParams.children("display_scores").text()=="yes"),
					bDisplayStats: (oCurObj.jxParams.children("display_statistics").text()=="yes"),
					bDisplayTimers: (oCurObj.jxParams.children("display_timers").text()=="yes"),
					bDisplayVariants: (oCurObj.jxParams.children("display_variants").text()=="yes"),
					sType: oCurObj.jxParams.children("display_type").text()
				};

				if(oCurObj.data.sType=="detail")
				{
					var jxObjList = oCurObj.jxParams.children("objects").children("item");
					if(jxObjList.length!=0)
					{
						var jContainer = jCurObjDiv.find(".cl-results-container");
						var jQTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-q-template");
						var jTTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-t-template");
						jxObjList.each(function ()
						{
							var sId = $(this).children("objid").text();
							var jxObject = CL.jxModule.find("object[id='" + sId + "']");
							var bDisplayHeader = ($(this).children("displayobjheader").text()=="yes");
							var jQBlock;
							if(jxObject.length>0)
							{
								var sType = jxObject.attr("type");
								if(sType=="q_0000" || sType=="q_0001")
								{
									if(sType=="q_0001")
									{
										var jTBlock = jTTemplate.clone(true).attr({ "data-o-id": sId, "data-o-type": sType, "data-o-toggle": "1" }).appendTo(jContainer).show();
										if(bDisplayHeader)
										{
											jTBlock.find(".cl-results-t-header .cl-results-header-title").html($(this).children("objheader").text());
										}
										else
										{
											jTBlock.find(".cl-results-t-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_test").text() + " " + sId);
										}
										if(CLZ.oStore[sId]==null)
										{
											jTBlock.find(".cl-results-t-header .cl-switch").hide();
											jTBlock.find(".cl-results-t-contents").hide();
											jTBlock.find(".cl-results-t-header .cl-results-na").show();
										}
										else
										{
											jTBlock.find(".cl-results-t-header .cl-switch").show();
											jTBlock.find(".cl-results-t-contents").show();
											jTBlock.find(".cl-results-t-header .cl-results-na").hide();
										}
									}
									else
									{
										jParent = jContainer;
										jQBlock = jQTemplate.clone(true).attr({ "data-o-id": sId, "data-o-type": sType, "data-o-toggle": "1"  }).appendTo(jParent).show();
										if(bDisplayHeader)
										{
											jQBlock.find(".cl-results-header .cl-results-header-title").html($(this).children("objheader").text());
										}
										else
										{
											jQBlock.find(".cl-results-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_question").text() + " " + sId);
										}
										if(CLZ.oStore[sId]==null)
										{
											jQBlock.find(".cl-switch").hide();
											jQBlock.find(".cl-results-contents").hide();
											jQBlock.find(".cl-results-na").show();
											jQBlock.find(".cl-results-header-score").hide();
										}
										else
										{
											if(CLZ.oStore[sId].u=="i")
											{
												jQBlock.find(".cl-switch").hide();
												jQBlock.find(".cl-results-contents").hide();
												jQBlock.find(".cl-results-na").show();
												jQBlock.find(".cl-results-header-score").hide();
											}
											else if(CLZ.oStore[sId].u=="c")
											{
												jQBlock.find(".cl-switch").show();
												jQBlock.find(".cl-results-contents").show();
												jQBlock.find(".cl-results-na").hide();
												jQBlock.find(".cl-results-header-score").show();
											}
										}
									}
								}
							}
						});
						jContainer.find("div.cl-switch").css({ "cursor": "pointer" }).on("click", function (e) { oCurObj.CallMethod({ sMethod: "Toggle", oMethodArgs: { oElem: this, oEvt: e } }) });
					}
				}

				oCurObj.data.bCreated = true;
				oCurObj.FireEvent({ sName: "onCreated" });
				if(!bDefer)
				{
					oCurObj.FireEvent({ sName: "onStartDisplay" });
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".container:first").hide();
				}
				return true;
			}

function Toggle(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null || argobj.oElem==null) return false;
				var jParent = $(argobj.oElem).parents(".cl-results-parent:first");
				var jContent = jParent.children(".cl-results-contents");
				if(jContent.attr("data-in-trans")!=null) return false;
				if(jContent.is(":visible"))
				{
					jContent.attr({ "data-in-trans": "1" }).slideUp(400, function () { this.removeAttribute("data-in-trans") });
					$(argobj.oElem).find(".cl-switch-path-minus").hide();
					$(argobj.oElem).find(".cl-switch-path-plus").show();
				}
				else
				{
					jContent.attr({ "data-in-trans": "1" }).slideDown(400, function () { this.removeAttribute("data-in-trans") });
					$(argobj.oElem).find(".cl-switch-path-plus").hide();
					$(argobj.oElem).find(".cl-switch-path-minus").show();
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null) return false;
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.FireEvent({ sName: "onStartDisplay" });
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				oCurObj.CallMethod({ sMethod: "Refresh" });
				jCurObjDiv.find(".container:first").show();
				return true;
			}

function Refresh(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(oCurObj.data.sType)
				{
					case "plain":
					{
						var sObjId = oCurObj.jxParams.children("q_board").text();
						if(sObjId=="") return false;
						var nScore = +CLJ[sObjId].nRawScore;
						var nMaxScore = +CLJ[sObjId].nMax;
						var sPlainType = oCurObj.jxParams.children("plain_values").text();
						var sD1 = "";
						var sD2 = "";
						var nPercent = (nMaxScore==0) ? 0 : Math.round(100*nScore/nMaxScore);
						switch(sPlainType)
						{
							case "score":
							{
								sD1 = nScore.toString();
								sD2 = nMaxScore.toString();
								break;
							}
							case "percent":
							{
								sD1 = nPercent + "%";
								sD2 = "100%";
								break;
							}
							case "score_percent":
							{
								sD1 = nScore.toString() + " (" + nPercent + "%)";
								sD2 = nMaxScore.toString() + " (100%)";
								break;
							}
						}
						var sString = oCurObj.jxParams.children("plain_text").text();
						var aParts = sString.split("{{1}}");
						sString = aParts.join(sD1);
						aParts = sString.split("{{2}}");
						jCurObjDiv.find(".cl-table-plain-td").html(aParts.join(sD2));
						break;
					}
					case "scale":
					{
						var jxObjs = oCurObj.jxParams.find("scoreboards > item");
						if(jxObjs.length==0) return false;
						var jxItem;
						var nScore;
						var nMaxScore;
						var sScaleType = oCurObj.jxParams.children("scale_values").text();
						var sObjId;
						var sD1 = "";
						var sD2 = "";
						var nPercent;
						var sString = oCurObj.jxParams.children("scale_text").text();
						var sTmpString;
						for(var i=1; i<=jxObjs.length; i++)
						{
							jxItem = $(jxObjs[i-1]);
							sObjId = jxItem.children("scoreboard").text();
							if(sObjId=="") continue;
							nScore = +CLJ[sObjId].nRawScore;
							nMaxScore = +CLJ[sObjId].nMax;
							nPercent = (nMaxScore==0) ? 0 : Math.round(100*nScore/nMaxScore);
							switch(sScaleType)
							{
								case "score":
								{
									sD1 = nScore.toString();
									sD2 = nMaxScore.toString();
									break;
								}
								case "percent":
								{
									sD1 = nPercent + "%";
									sD2 = "100%";
									break;
								}
								case "score_percent":
								{
									sD1 = nScore.toString() + " (" + nPercent + "%)";
									sD2 = nMaxScore.toString() + " (100%)";
									break;
								}
							}
							sTmpString = sString;
							var aParts = sTmpString.split("{{1}}");
							sTmpString = aParts.join(sD1);
							aParts = sTmpString.split("{{2}}");
							jCurObjDiv.find(".cl-table-scale-h-value-" + i).html(aParts.join(sD2));
							jCurObjDiv.find(".cl-table-scale-h-scale-" + i + " .cl-table-scale-h-ind").css({ "margin-left": "-" + (100-nPercent) + "%" });
						}
						break;
					}
					case "detail":
					{
						function fnGetText(oArgs)
						{
							var jDiv = $(document.createElement("div")).html(oArgs.sHTML);
							var sTxt = jDiv.text();
							jDiv.remove();
							return sTxt;
						}
						function fnFillVars(oArgs)
						{
							// jVarTable, jxParams, sId, oStore
							var jRow;
							var jCell;
							switch(oArgs.sType)
							{
								case "true-false":
								case "choice":
								case "select":
								{
									jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").attr({ "colspan": "2" }).html(oCurObj.jxParams.children("text_constant_youranswer").text()).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_correct").text()).appendTo(jRow);
									}
									break;
								}
								case "order":
								case "numeric":
								case "fib":
								{
									jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_youranswer").text()).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_correct").text()).appendTo(jRow);
									}
									break;
								}
								case "oto":
								{
									jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_targets").text()).appendTo(jRow);
									$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_bullets").text()).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										$(document.createElement("td")).addClass("cl-results-q-variants-header").html(oCurObj.jxParams.children("text_constant_correct").text()).appendTo(jRow);
									}
									break;
								}
							}
							switch(oArgs.sType)
							{
								case "true-false":
								{
									jRow = $(document.createElement("tr")).attr({ "data-var-id": "true" }).appendTo(oArgs.jVarTable);
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]==1)
											{
												jCell.addClass("cl-results-mark-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]==1)
													{
														jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
													}
													else
													{
														jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
													}
												}
												else
												{
													jCell.html("&#10003;");
												}
											}
										}
									}
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: oArgs.jxParams.children("q_text_true").text() })).appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]==1)
											{
												jCell.addClass("cl-results-var-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]==1)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
										}
									}
									if(oCurObj.data.bDisplayCorrects)
									{
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(+oArgs.oStore.P[0]==1)
										{
											jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
										}
									}
									jRow = $(document.createElement("tr")).attr({ "data-var-id": "false" }).appendTo(oArgs.jVarTable);
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]!=1)
											{
												jCell.addClass("cl-results-mark-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]!=1)
													{
														jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
													}
													else
													{
														jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
													}
												}
												else
												{
													jCell.html("&#10003;");
												}
											}
										}
									}
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").text(fnGetText({ sHTML: oArgs.jxParams.children("q_text_false").text() })).appendTo(jRow);
									if(oArgs.oStore.R!=null)
									{
										if(oArgs.oStore.R[0]!=null)
										{
											if(+oArgs.oStore.R[0]!=1)
											{
												jCell.addClass("cl-results-var-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.P[0]!=1)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
										}
									}
									if(oCurObj.data.bDisplayCorrects)
									{
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(+oArgs.oStore.P[0]!=1)
										{
											jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
										}
									}
									break;
								}
								case "choice":
								{
									var jxVars = oArgs.jxParams.find("q_variants_choice:first > item");
									for(var k=1; k<=jxVars.length; k++)
									{
										jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if(oArgs.oStore.R[0]!=null)
											{
												if(+oArgs.oStore.R[0]==k)
												{
													jCell.addClass("cl-results-mark-selected");
													if(oCurObj.data.bDisplayCorrects)
													{
														if(+oArgs.oStore.P[0]==k)
														{
															jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
														}
														else
														{
															jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
														}
													}
													else
													{
														jCell.html("&#10003;");
													}
												}
											}
										}
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if(oArgs.oStore.R[0]!=null)
											{
												if(+oArgs.oStore.R[0]==k)
												{
													jCell.addClass("cl-results-var-selected");
													if(oCurObj.data.bDisplayCorrects)
													{
														if(+oArgs.oStore.P[0]==k)
														{
															jCell.addClass("cl-results-correct");
														}
														else
														{
															jCell.addClass("cl-results-incorrect");
														}
													}
												}
											}
										}
										if(oCurObj.data.bDisplayCorrects)
										{
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
											if(+oArgs.oStore.P[0]==k)
											{
												jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
											}
										}
									}
									break;
								}
								case "select":
								{
									var jxVars = oArgs.jxParams.find("q_variants_select:first > item");
									for(var k=1; k<=jxVars.length; k++)
									{
										jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if($.inArray(k, oArgs.oStore.R)!=-1)
											{
												jCell.addClass("cl-results-mark-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if($.inArray(k, oArgs.oStore.P)!=-1)
													{
														jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
													}
													else
													{
														jCell.addClass("cl-results-mark-incorrect cl-results-incorrect").html("&#10007;")
													}
												}
												else
												{
													jCell.html("&#10003;");
												}
											}
										}
										jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
										if(oArgs.oStore.R!=null)
										{
											if($.inArray(k, oArgs.oStore.R)!=-1)
											{
												jCell.addClass("cl-results-var-selected");
												if(oCurObj.data.bDisplayCorrects)
												{
													if($.inArray(k, oArgs.oStore.P)!=-1)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
										}
										if(oCurObj.data.bDisplayCorrects)
										{
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-mark").html("&nbsp;").appendTo(jRow);
											if($.inArray(k, oArgs.oStore.P)!=-1)
											{
												jCell.addClass("cl-results-mark-correct cl-results-correct").html("&#10003;");
											}
										}
									}
									break;
								}
								case "order":
								{
									var jxVars = oArgs.jxParams.find("q_variants_order:first > item");
									var iIdx = -1;
									if(oArgs.oStore.R!=null && oArgs.oStore.R.length==jxVars.length)
									{
										for(var k=1; k<=jxVars.length; k++)
										{
											iIdx = +oArgs.oStore.R[k-1];
											jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[iIdx-1]).children("q_variant").text() })).appendTo(jRow);
											if(oCurObj.data.bDisplayCorrects)
											{
												if(iIdx==k)
												{
													jCell.addClass("cl-results-correct");
												}
												else
												{
													jCell.addClass("cl-results-incorrect");
												}
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
											}
										}
									}
									break;
								}
								case "numeric":
								case "fib":
								{
									var jxVars = oArgs.jxParams.find("q_variants_" + oArgs.sType + ":first > item");
									var jxCond;
									var sOp;
									var aCond;
									var oSigns = { "eq": "=", "ne": "!=", "lt": "&lt;", "lte": "&lt;=", "gt": "&gt;", "gte": "&gt;=" };
									if(oArgs.oStore.R!=null && oArgs.oStore.R.length!=0)
									{
										for(var k=1; k<=jxVars.length; k++)
										{
											jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(oArgs.oStore.R[k-1]).appendTo(jRow);
											if(oCurObj.data.bDisplayCorrects)
											{
												if(CL.Q.EvalSet({ sType: oArgs.sType, sValue: oArgs.oStore.R[k-1], oPattern: oArgs.oStore.P[k-1] })==true)
												{
													jCell.addClass("cl-results-correct");
												}
												else
												{
													jCell.addClass("cl-results-incorrect");
												}
												aCond = [];
												sOp = $(jxVars[k-1]).children("q_cond_logic").text();
												jxCond = $(jxVars[k-1]).find("q_conditions > item");
												if(oArgs.sType=="numeric")
												{
													for(var j=0; j<jxCond.length; j++)
													{
														aCond.push( oSigns[$(jxCond[j]).children("q_condition").text()] + $(jxCond[j]).children("q_variant").text() );
													}
												}
												else
												{
													for(var j=0; j<jxCond.length; j++)
													{
														switch($(jxCond[j]).children("q_condition").text())
														{
															case "eq":
															{
																aCond.push( "=" + $(jxCond[j]).children("q_variant").text() );
																break;
															}
															case "bw":
															{
																aCond.push( $(jxCond[j]).children("q_variant").text() + "*");
																break;
															}
															case "eq":
															{
																aCond.push( "*" + $(jxCond[j]).children("q_variant").text() );
																break;
															}
															case "eq":
															{
																aCond.push( "*" + $(jxCond[j]).children("q_variant").text() + "*" );
																break;
															}
														}
													}
												}
												$(document.createElement("td")).addClass("cl-results-q-variants-text").html( aCond.join(" " + sOp + " ") ).appendTo(jRow);
											}
										}
									}
									break;
								}
								case "oto":
								{
									var jxVars = oArgs.jxParams.find("q_variants_oto:first > item");
									if(oArgs.oStore.R!=null && oArgs.oStore.R.length!=0)
									{
										for(var k=1; k<=jxVars.length; k++)
										{
											jRow = $(document.createElement("tr")).attr({ "data-var-id": k }).appendTo(oArgs.jVarTable);
											jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_target").text() })).appendTo(jRow);
											if(oCurObj.data.bDisplayCorrects)
											{
												if(+oArgs.oStore.R[k-1]==k)
												{
													jCell.addClass("cl-results-correct");
												}
												else
												{
													jCell.addClass("cl-results-incorrect");
												}
											}
											if(oArgs.oStore.R[k-1]!="" && !isNaN(+oArgs.oStore.R[k-1]))
											{
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[+oArgs.oStore.R[k-1]-1]).children("q_bullet").text() })).appendTo(jRow);
												if(oCurObj.data.bDisplayCorrects)
												{
													if(+oArgs.oStore.R[k-1]==k)
													{
														jCell.addClass("cl-results-correct");
													}
													else
													{
														jCell.addClass("cl-results-incorrect");
													}
												}
											}
											else
											{
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html("&nbsp;").appendTo(jRow);
											}
											if(oCurObj.data.bDisplayCorrects)
											{
												jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_bullet").text() })).appendTo(jRow);
											}
										}
									}
									break;
								}
							}
						}

						var jxObjList = oCurObj.jxParams.children("objects").children("item");
						if(jxObjList.length!=0 && oCurObj.data.bCreated)
						{
							var jContainer = jCurObjDiv.find(".cl-results-container");
							var jQTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-q-template");
							var jTTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-t-template");
							var jQBlock;
							var jTBlock;
							var sMainObj = "";
							if(oCurObj.data.bDisplayMainScore)
							{
								for(var sKey in CLJ)
								{
									if(+CLJ[sKey].bModule==1)
									{
										sMainObj = sKey;
										break;
									}
								}
							}
							var jTxtDiv = $(document.createElement("div"));
							jxObjList.each(function ()
							{
								var sId = $(this).children("objid").text();
								var jxObject = CL.jxModule.find("object[id='" + sId + "']");
								var jxParams = CL.jxParams.children("param[objectid='" + sId + "']");
								var bDisplayHeader = ($(this).children("displayobjheader").text()=="yes");
								var jxObjectives;
								var sTxt;
								if(jxObject.length>0)
								{
									var sType = jxObject.attr("type");
									if(sType=="q_0000" || sType=="q_0001")
									{
										if(sType=="q_0001")
										{
											if(CLZ.oStore[sId]!=null)
											{
												var jTBlock = jContainer.find("div[data-o-id='" + sId + "']");
												var jContents = jTBlock.children(".cl-results-t-contents").html("");
												var jxTestParams = CL.jxParams.children("param[objectid='" + sId + "']");
												var jxQuestions = jxTestParams.find("q_questions:first > item");
												var bRandom = (jxTestParams.find("randomize").text()=="yes");
												var iToSelect = +jxTestParams.find("random_quantity").text();
												var iQuestionsInTest = (bRandom ? (iToSelect>jxQuestions.length ? jxQuestions.length : iToSelect) : jxQuestions.length);
												var jxQuestion;
												var sQId;
												var jxQParams;
												var iIdx;
												var sType;
												var oOldType;
												var oScoring;
												var sScoring;
												var iAttemptsLeft;
												var sObjId;
												var jRow;
												var jCell;
												var jScoreTable;
												var jVarTable;
												for(var i=0; i<iQuestionsInTest; i++)
												{
													iIdx = (bRandom ? CLZ.oStore[sId].P[i] : i);
													sQId = sId + "_" + iIdx;
													jQBlock = jQTemplate.clone(true).addClass("cl-results-q").attr({ "data-o-id": sId, "data-o-type": sType, "data-q-id": iIdx, "data-o-toggle": "1" }).appendTo(jContents).show();
													if(bDisplayHeader)
													{
														jQBlock.find(".cl-results-header .cl-results-header-title").html($(this).children("objheader").text() + " (" + (i+1) + "/" + iQuestionsInTest + ")");
													}
													else
													{
														jQBlock.find(".cl-results-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_test").text() + " " + sId + " (" + (i+1) + "/" + iQuestionsInTest + ")");
													}
													if(CLZ.oStore[sId].Q[i]!=null)
													{
														jxQParams = $(jxQuestions[iIdx]);
														switch(CLZ.oStore[sId].Q[i].u)
														{
															case "c":
															{
																jQBlock.find(".cl-switch").show().on("click", function (e) { oCurObj.CallMethod({ sMethod: "Toggle", oMethodArgs: { oElem: this, oEvt: e } }) });
																jQBlock.find(".cl-results-contents").show();
																jQBlock.find(".cl-results-na").hide();
																jQBlock.find(".cl-results-header-score").show();
																sType = jxQParams.children("question_type").text();
																if(sMainObj!="")
																{
																	if(CLJ[sMainObj]!=null)
																	{
																		if(oCurObj.data.bDisplayMainScore && CLJ[sMainObj].oSrc[sQId]!=null)
																		{
																			jQBlock.find(".cl-results-header-score-div").html(CLJ[sMainObj].oSrc[sQId].nRawScore);
																		}
																	}
																}
																if(oCurObj.data.bDisplayQuestion)
																{
																	jQBlock.find(".cl-results-q-text").html(fnGetText({ sHTML: jxQParams.children("text_main").text() }));
																}
																if(oCurObj.data.bDisplayStats)
																{
																	oOldType = { "true-false": "tf", "choice": "choice", "select": "select", "order": "range", "numeric": "numeric", "fib": "text", "oto": "oto", "otm": "otm", "mtm": "mtm" };
																	oScoring = { "true-false": "tf", "choice": "choice", "select": "choice", "order": "choice", "numeric": "choice", "fib": "choice", "oto": "choice", "otm": "choice", "mtm": "choice" };
																	jQBlock.find(".cl-stats-q-type").html(oCurObj.jxParams.children("text_objecttype_" + oOldType[sType]).text());
																	sScoring = jxQParams.children("q_scoring_type_" + oScoring[sType]).text();
																	jQBlock.find(".cl-stats-q-scoring").html(oCurObj.jxParams.children("text_constant_scoringtype").text() + " " + oCurObj.jxParams.children("text_scoringtype_" + sScoring).text());
																	if(oCurObj.data.bDisplayAttempts)
																	{
																		iAttemptsLeft = 0;
																		for(var j=0; j<CLZ.oStore[sId].Q[i].A.length; j++)
																		{
																			if(CLZ.oStore[sId].Q[i].A[j].u=="c") continue;
																			iAttemptsLeft = CLZ.oStore[sId].Q[i].A.length - j;
																			break;
																		}
																		jQBlock.find(".cl-stats-q-attempts").html(oCurObj.jxParams.children("text_constant_attemptsmax").text() + " " + CLZ.oStore[sId].Q[i].A.length + "<br/>" + oCurObj.jxParams.children("text_constant_attemptsleft").text() + " " + iAttemptsLeft);
																	}
																	if(oCurObj.data.bDisplayTimers && CLZ.oStore[sId].Q[i].t!=null)
																	{
																		jQBlock.find(".cl-stats-q-timing").html(oCurObj.jxParams.children("text_constant_duration").text() + " " + CLZ.oStore[sId].Q[i].t + "<br/>" + oCurObj.jxParams.children("text_constant_latency").text() + " " + CLZ.oStore[sId].Q[i].e);
																	}
																	else
																	{
																		jQBlock.find(".cl-stats-q-timing").hide();
																	}
																	if(oCurObj.data.bDisplayScores)
																	{
																		jxObjectives = jxQParams.find("q_scoreboards > item");
																		jScoreTable = jQBlock.find(".cl-stats-obj-table");
																		if(jScoreTable.children("tbody").length!=0)
																		{
																			jScoreTable = jScoreTable.children("tbody:first");
																		}
																		for(var j=0; j<jxObjectives.length; j++)
																		{
																			sObjId = $(jxObjectives[j]).children("q_board").text();
																			jRow = jScoreTable.children("tr[data-obj-id='" + sObjId + "']");
																			if(jRow.length==0)
																			{
																				jRow = $(document.createElement("tr")).attr({ "data-obj-id": sObjId }).appendTo(jScoreTable);
																				$(document.createElement("td")).addClass("cl-stats-obj-obj").attr({ "data-obj-id": sObjId }).html(CLJ[sObjId].sName).appendTo(jRow);
																				$(document.createElement("td")).addClass("cl-stats-obj-score").attr({ "data-obj-id": sObjId }).appendTo(jRow);
																			}
																			if(CLJ[sObjId].oSrc[sQId]!=null)
																			{
																				jRow.children(".cl-stats-obj-score").html(CLJ[sObjId].oSrc[sQId].nRawScore);
																			}
																			else
																			{
																				jRow.children(".cl-stats-obj-score").html("0");
																			}
																		}
																	}
																	else
																	{
																		jQBlock.find(".cl-stats-right").hide();
																	}
																}
																if(oCurObj.data.bDisplayVariants)
																{
																	jVarTable = jQBlock.find(".cl-results-q-variants-table");
																	if(jVarTable.length!=0)
																	{
																		jVarTable.remove();
																	}
																	jVarTable = $(document.createElement("table")).addClass("cl-results-q-variants-table").appendTo(jQBlock.find(".cl-results-variants .cl-results-contents"));
																	jVarTable = $(document.createElement("tbody")).appendTo(jVarTable);
																	fnFillVars({ sId: sQId, sType: sType, jVarTable: jVarTable, jxParams: jxQParams, oStore: CLZ.oStore[sId].Q[i] });
																}
																break;
															}
															case "i":
															{
																jQBlock.find(".cl-switch").hide();
																jQBlock.find(".cl-results-contents").hide();
																jQBlock.find(".cl-results-na").show();
																jQBlock.find(".cl-results-header-score").hide();
																break;
															}
														}
													}
													else
													{
														jQBlock.find(".cl-switch").hide();
														jQBlock.find(".cl-results-contents").hide();
														jQBlock.find(".cl-results-na").show();
														jQBlock.find(".cl-results-header-score").hide();
													}
												}
											}
										}
										else
										{
											jQBlock = jContainer.find("div[data-o-id='" + sId + "']");
											if(CLZ.oStore[sId]!=null)
											{
												switch(CLZ.oStore[sId].u)
												{
													case "c":
													{
														var sType = jxParams.children("question_type").text();
														if(sMainObj!="")
														{
															if(CLJ[sMainObj]!=null)
															{
																if(oCurObj.data.bDisplayMainScore && CLJ[sMainObj].oSrc[sId]!=null)
																{
																	jQBlock.find(".cl-results-header-score-div").html(CLJ[sMainObj].oSrc[sId].nRawScore);
																}
															}
														}
														if(oCurObj.data.bDisplayQuestion)
														{
															jTxtDiv.html(jxParams.children("text_main").text());
															jQBlock.find(".cl-results-q-text").html(jTxtDiv.text());
														}
														if(oCurObj.data.bDisplayStats)
														{
															var oOldType = { "true-false": "tf", "choice": "choice", "select": "select", "order": "range", "numeric": "numeric", "fib": "text", "oto": "oto", "otm": "otm", "mtm": "mtm" };
															var oScoring = { "true-false": "tf", "choice": "choice", "select": "select", "order": "match", "numeric": "inputs", "fib": "inputs", "oto": "match", "otm": "match", "mtm": "match" };
															jQBlock.find(".cl-stats-q-type").html(oCurObj.jxParams.children("text_objecttype_" + oOldType[sType]).text());
															var sScoring = jxParams.children("q_scoring_type_" + oScoring[sType]).text();
															jQBlock.find(".cl-stats-q-scoring").html(oCurObj.jxParams.children("text_constant_scoringtype").text() + " " + oCurObj.jxParams.children("text_scoringtype_" + sScoring).text());
															if(oCurObj.data.bDisplayAttempts)
															{
																var iAttemptsLeft = 0;
																for(var i=0; i<CLZ.oStore[sId].A.length; i++)
																{
																	if(CLZ.oStore[sId].A[i].u=="c") continue;
																	iAttemptsLeft = CLZ.oStore[sId].A.length - i;
																	break;
																}
																jQBlock.find(".cl-stats-q-attempts").html(oCurObj.jxParams.children("text_constant_attemptsmax").text() + " " + CLZ.oStore[sId].A.length + "<br/>" + oCurObj.jxParams.children("text_constant_attemptsleft").text() + " " + iAttemptsLeft);
															}
															if(oCurObj.data.bDisplayTimers && CLZ.oStore[sId].t!=null)
															{
																jQBlock.find(".cl-stats-q-timing").html(oCurObj.jxParams.children("text_constant_duration").text() + " " + CLZ.oStore[sId].t + "<br/>" + oCurObj.jxParams.children("text_constant_latency").text() + " " + CLZ.oStore[sId].e);
															}
															else
															{
																jQBlock.find(".cl-stats-q-timing").hide();
															}
															if(oCurObj.data.bDisplayScores)
															{
																jxObjectives = jxParams.find("q_scoreboards > item");
																var sObjId;
																var jRow;
																var jCell;
																var jScoreTable = jQBlock.find(".cl-stats-obj-table");
																if(jScoreTable.children("tbody").length!=0)
																{
																	jScoreTable = jScoreTable.children("tbody:first");
																}
																for(var i=0; i<jxObjectives.length; i++)
																{
																	sObjId = $(jxObjectives[i]).children("q_board").text();
																	jRow = jScoreTable.children("tr[data-obj-id='" + sObjId + "']");
																	if(jRow.length==0)
																	{
																		jRow = $(document.createElement("tr")).attr({ "data-obj-id": sObjId }).appendTo(jScoreTable);
																		$(document.createElement("td")).addClass("cl-stats-obj-obj").attr({ "data-obj-id": sObjId }).html(CLJ[sObjId].sName).appendTo(jRow);
																		$(document.createElement("td")).addClass("cl-stats-obj-score").attr({ "data-obj-id": sObjId }).appendTo(jRow);
																	}
																	if(CLJ[sObjId].oSrc[sId]!=null)
																	{
																		jRow.children(".cl-stats-obj-score").html(CLJ[sObjId].oSrc[sId].nRawScore);
																	}
																	else
																	{
																		jRow.children(".cl-stats-obj-score").html("0");
																	}
																}
															}
															else
															{
																jQBlock.find(".cl-stats-right").hide();
															}
														}
														if(oCurObj.data.bDisplayVariants)
														{
															var jVarTable = jQBlock.find(".cl-results-q-variants-table");
															if(jVarTable.length!=0)
															{
																jVarTable.remove();
															}
															jVarTable = $(document.createElement("table")).addClass("cl-results-q-variants-table").appendTo(jQBlock.find(".cl-results-variants .cl-results-contents"));
															jVarTable = $(document.createElement("tbody")).appendTo(jVarTable);
															fnFillVars({ sId: sId, sType: sType, jVarTable: jVarTable, jxParams: jxParams, oStore: CLZ.oStore[sId] });
														}
														break;
													}
													case "i":
													{
														jQBlock.find(".cl-results-na").html("Не закончен");
														break;
													}
												}
											}
										}
									}
								}
							});
						}
						break;
					}
				}
				return true;
			}
