methods.method:
function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				var sRole = oCurObj.jxParams.children("role").text();
				var sTarget = (sRole.indexOf("slide")==-1) ? "frame" : "slide";
				var sDir = "";
				var sId;
				if(sRole=="closemodule")
				{
					oCurObj.data =
					{
						sRole: "close",
						sState: "enabled",
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
				}
				else
				{
					var bSwipe = false;
					if(sRole.indexOf("next")!=-1)
					{
						sDir = "next";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("prev")!=-1)
					{
						sDir = "prev";
						bSwipe = (oCurObj.jxParams.children("swipe").text()=="yes");
					}
					else if(sRole.indexOf("first")!=-1)
					{
						sDir = "first";
					}
					else if(sRole.indexOf("last")!=-1)
					{
						sDir = "last";
					}
					else if(sRole=="thisslide")
					{
						sDir = "this";
					}
					else
					{
						sId = oCurObj.jxParams.children((sRole=="selectedslide") ? "selectedslide" : "selectedframe").text();
					}
					oCurObj.data =
					{
						sRole: sRole,
						sTarget: sTarget,
						sDir: sDir,
						sId: sId,
						sFrameId: "",
						sSlideId: "",
						sState: "enabled",
						bNaviStrict: (oCurObj.jxParams.children("nav_lock").text()=="yes"),
						bNaviLockedByAction: false,
						sSoundClick: oCurObj.jxParams.children("sound_click").text(),
						sSoundOver: oCurObj.jxParams.children("sound_over").text()
					};
					jCurObjDiv.addClass("cl-nav-action-" + oCurObj.data.sRole);
					if(oCurObj.data.sSoundClick=="custom")
					{
						oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
					}
					if(oCurObj.data.sSoundOver=="custom")
					{
						oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
					}
					if(oCurObj.data.sRole=="close")
					{
						oCurObj.data.bDisplayConfirm = (oCurObj.jxParams.children("display_closemodule_msg").text()=="yes");
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_FRAME_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" }, { sId: argobj.pid, sEvtName: "EVENT_FRAME_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_SLIDE_COMPLETE" }, { sId: argobj.pid, sEvtName: "EVENT_NAVIGATION" } ] });
				}
				if(CL.bTouch)
				{
					if(bSwipe)
					{
						if(sDir=="next")
						{
							$(CL.oBoard).on("swipeleft", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}
						else if(sDir=="prev")
						{
							$(CL.oBoard).on("swiperight", function (e) { oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); e.originalEvent.stopPropagation(); return false; });
						}

					}
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("click", function () { oCurObj.CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				else
				{
					jCurObjDiv.find(".cl-btn").addClass("cl-idle").on("mouseover", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "over" } }) }).on("mouseout", function () { oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { pid: argobj.pid, sState: "out" } }) }).on("click", function () { CLO[argobj.pid].CallMethod({ sMethod: "Click", oMethodArgs: { pid: argobj.pid } }) });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sTarget=="slide")
				{
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_SLIDE_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets==oCurObj.data.sDir || (argobj.sTargets=="restart" && oCurObj.data.sDir=="this") || argobj.sTargets=="all" || (argobj.sTargets=="buttons" && (oCurObj.data.sDir=="next" || oCurObj.data.sDir=="prev")) || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "update":
										{
											oCurObj.CallMethod({ sMethod: "Update" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
				}
				else
				{
					switch(argobj.evt)
					{
						case "EVENT_FRAME_OPENED":
						{
							if(oCurObj.data.sFrameId!=CLZ.sCurrentFrameId)
							{
								oCurObj.data.sFrameId = CLZ.sCurrentFrameId;
								oCurObj.data.bNaviLockedByAction = false;
							}
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviStrict || oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_FRAME_COMPLETE":
						{
							oCurObj.CallMethod({ sMethod: ((oCurObj.data.bNaviLockedByAction) ? "DisableAccess" : "EnableAccess") });
							break;
						}
						case "EVENT_NAVIGATION":
						{
							if(argobj.sTargets!=null)
							{
								if(argobj.sTargets=="all" || argobj.sTargets=="buttons" || argobj.sTargets=="allbuttons")
								{
									switch(argobj.sAction)
									{
										case "off":
										{
											oCurObj.data.bNaviLockedByAction = true;
											oCurObj.CallMethod({ sMethod: "DisableAccess" });
											break;
										}
										case "on":
										default:
										{
											oCurObj.data.bNaviLockedByAction = false;
											oCurObj.CallMethod({ sMethod: "EnableAccess" });
											break;
										}
									}
								}
							}
							break;
						}
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv= $(oCurObj.div);
				if(argobj.evt==null) return false;
				if(oCurObj.data.sState=="disabled" || $(".cl-modal-mask").is(":visible")) return false;
				switch(argobj.evt.type)
				{
					case "swiperight":
					case "swipeleft":
					{
						oCurObj.CallMethod({ sMethod: "Navigate" });
						break;
					}
				}
				return true;
			}

function SetState(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jBtn = $(CLO[argobj.pid].div).find(".cl-btn");
				if(jBtn.hasClass("cl-disabled") && argobj.sState!="enabled") return false;
				switch(argobj.sState)
				{
					case "over":
					{
						jBtn.removeClass("cl-idle").addClass("cl-over");
						CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
						if(oCurObj.data.sSoundOver!="none")
						{
							CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
						}
						break;
					}
					case "out":
					{
						jBtn.removeClass("cl-over").addClass("cl-idle");
						break;
					}
					case "disabled":
					{
						jBtn.removeClass("cl-idle cl-over").addClass("cl-disabled");
						CLO[argobj.pid].data.sState = "disabled";
						break;
					}
					case "enabled":
					{
						jBtn.removeClass("cl-disabled").addClass("cl-idle");
						CLO[argobj.pid].data.sState = "enabled";
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
				return true;
			}

function EnableAccess(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(CLO[argobj.pid].data.sRole=="close")
				{
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "enabled" } });
				}
				else
				{
					var oParams = { sTarget: CLO[argobj.pid].data.sTarget };
					if(CLO[argobj.pid].data.sDir=="")
					{
						oParams.sTargetId = CLO[argobj.pid].data.sId;
					}
					else
					{
						oParams.sDir = CLO[argobj.pid].data.sDir;
					}
					CLO[argobj.pid].CallMethod({ sMethod: "SetState", oMethodArgs: { sState: ( (argobj.override==true || CL.Navigation.Allowed(oParams) || (CL.bAlternativePath && CL.Navigation.Exists(oParams) )) ? "enabled" : "disabled") } });
				}
				return true;
			}

function Navigate(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var oParams = { sTargetType: oCurObj.data.sTarget, bUserAction: true };
				if(oCurObj.data.sDir=="")
				{
					oParams.sTargetId = oCurObj.data.sId;
				}
				else
				{
					oParams.sTargetDir = oCurObj.data.sDir;
				}
				var sCLXNavi = "";
				CLS[CLZ.sCurrentSlideId].jxObjects.each(function()
				{
					if(this.getAttribute("type")=="clx_navigation")
					{
						sCLXNavi = this.getAttribute("id");
					}
				});
				if(sCLXNavi!="")
				{
					if((oCurObj.data.sDir=="next" && CLO[sCLXNavi].data.bCustomNext) || (oCurObj.data.sDir=="prev" && CLO[sCLXNavi].data.bCustomPrev))
					{
						return CLO[sCLXNavi].CallMethod({ sMethod: "ProcessNavigation", oMethodArgs: { sDir: oCurObj.data.sDir } });
					}
				}
				if(argobj.override==true || CL.Navigation.Allowed(oParams))
				{
					oCurObj.CallMethod({ sMethod: "SetState", oMethodArgs: { sState: "disabled" } });
					CL.Navigation.GoTo(oParams);
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
				if(oCurObj.data.sSoundClick!="none")
				{
					CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
				}
				return true;
			}

function GetProperty(argobj)
			{
				return CLO[argobj.pid].GetProperty(argobj);
			}

function Click(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.data.sState=="disabled") return false;
				if(oCurObj.data.sRole=="close")
				{
					if(oCurObj.data.bDisplayConfirm)
					{
					if(confirm(CLO[argobj.pid].jxParams.children("closemodule_msg").text()))
						{
							window.close();
						}
					}
					else
					{
						window.close();
					}
				}
				else
				{
					CLO[argobj.pid].CallMethod({ sMethod: "Navigate" });
				}
				return true;
			}

function Constructor(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(oCurObj.div);
					oCurObj.data =
					{
						jRail: jCurObjDiv.find("tr.cl-rail"),
						jTemplate: jCurObjDiv.find("td.cl-template"),
						sChapterId: "",
						sScope: oCurObj.jxParams.children("display_scope").text(),
						sSlideId: ""
					};
					if(!CL.oPath.bAlternative || oCurObj.data.sScope!="chapter")
					{
						oCurObj.data.aPath = CL.oPath._GetSlideList();
					var jCell;
						for(var i=0; i<oCurObj.data.aPath.length; i++)
					{
							jCell = oCurObj.data.jTemplate.clone(true).appendTo(oCurObj.data.jRail).show();
							jCell.removeClass("cl-template").addClass("cl-unvisited").attr({ "id": "CELL_" + oCurObj.data.aPath[i], "cl-id": oCurObj.data.aPath[i] });
						}
					}
					CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_SLIDE_OPENED" } ] });
                    return true;
                }

function HandleEvt(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					if(oCurObj==null) return false;
					switch(argobj.evt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							if(oCurObj.data.sSlideId!=CLZ.sCurrentSlideId)
							{
								oCurObj.CallMethod({ sMethod: "Update" });
								oCurObj.data.sSlideId = CLZ.sCurrentSlideId;
							}
							break;
						}
					}
					return true;
                }

function Update(argobj)
                {
					var oCurObj = CLO[argobj.pid];
					var jCurObjDiv = $(oCurObj.div);
					if(!CL.oPath.bAlternative || oCurObj.data.sScope!="chapter")
					{
						for(var i=0; i<oCurObj.data.aPath.length; i++)
						{
							if(oCurObj.data.aPath[i]==CLZ.sCurrentSlideId)
							{
								oCurObj.data.jRail.children("[cl-id='" + oCurObj.data.aPath[i] + "']").removeClass("cl-unvisited cl-visited").addClass("cl-current");
							}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: oCurObj.data.aPath[i] }))
							{
								oCurObj.data.jRail.children("[cl-id='" + oCurObj.data.aPath[i] + "']").removeClass("cl-unvisited cl-current").addClass("cl-visited");
							}
							else
							{
								oCurObj.data.jRail.children("[cl-id='" + oCurObj.data.aPath[i] + "']").removeClass("cl-current cl-visited").addClass("cl-unvisited");
							}
						}
					}
					else if(CL.oPath.bAlternative && oCurObj.data.sScope=="chapter")
					{
						var sCurrentChapterId = CL.oPath._GetChapterId();
						if(sCurrentChapterId!=oCurObj.data.sChapterId)
						{
							oCurObj.data.aPath = CL.oPath._GetSlideList({ bChapter: true });
							var jCell;
							oCurObj.data.jRail.find("[cl-id]").remove();
							for(var i=0; i<oCurObj.data.aPath.length; i++)
					{
								jCell = oCurObj.data.jTemplate.clone(true).appendTo(oCurObj.data.jRail).show();
								jCell.removeClass("cl-template").addClass("cl-unvisited").attr({ "id": "CELL_" + oCurObj.data.aPath[i], "cl-id": oCurObj.data.aPath[i] });
							}
							oCurObj.data.sChapterId = sCurrentChapterId;
						}
						for(var i=0; i<oCurObj.data.aPath.length; i++)
						{
							if(oCurObj.data.aPath[i]==CLZ.sCurrentSlideId)
							{
								oCurObj.data.jRail.children("[cl-id='" + oCurObj.data.aPath[i] + "']").removeClass("cl-unvisited cl-visited").addClass("cl-current");
						}
							else if(CL.SCO.IsVisited({ sBy: "slideid", sSlideId: oCurObj.data.aPath[i] }))
						{
								oCurObj.data.jRail.children("[cl-id='" + oCurObj.data.aPath[i] + "']").removeClass("cl-unvisited cl-current").addClass("cl-visited");
						}
						else
						{
								oCurObj.data.jRail.children("[cl-id='" + oCurObj.data.aPath[i] + "']").removeClass("cl-current cl-visited").addClass("cl-unvisited");
							}
						}
					}
					return true;
                }

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false; 
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				if(oCurObj.jxParams.children("hide_on_click").text()=="yes")
				{
					jCurObjDiv.find(".cl-btn-close-object").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
				}
				oCurObj.data =
				{
					bCreated: true,
					bTypeWriter: (oCurObj.jxParams.children("typewriter").text()=="yes")
				};
				if(oCurObj.data.bTypeWriter)
				{
					var sSpeed = oCurObj.jxParams.children("typewriter_speed").text();
					var iSpeed = (sSpeed=="") ? 10 : parseInt(sSpeed, 10);
					if(isNaN(iSpeed)) iSpeed = 10;
					oCurObj.data.iDelay = Math.round(1000/iSpeed);
				}
				var sHTML;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					sHTML = CL.Substitute.All({ sString: oCurObj.oStates[sState].sText });
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box-cell").html( sHTML );
				}
				if(CL.bTouch)
				{
					oCurObj.data.jTextBox.on("touchmove", function(e) { e.stopPropagation() }).addClass("cl-touch-scroll");
				}
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".cl-container:first").hide();
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null) return false; 
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				jCurObjDiv.find(".cl-container:first").show();
				if(oCurObj.data.bTypeWriter)
				{
					oCurObj.CallMethod({ sMethod: "TypeWriter", oMethodArgs: { oElem: jCurObjDiv.find("div[data-state='default'] .cl-txt-box-cell"), iDelay: oCurObj.data.iDelay, iItem: 0 } });
				}
				return true;
			}

function TypeWriter(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = $(argobj.oElem); 
				if(jElem.attr("data-wrapped")=="yes")
				{
					jElem.find(".cl-char").each(function () { $(this).contents().unwrap() });
				}
				jElem.attr({ "data-wrapped": "yes" }).html( CL.Common.WrapCharacters({ sHTML: jElem.html() }) );
				oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: jElem, iDelay: oCurObj.data.iDelay, iItem: 0 } });
				return true;
			}

function NextChar(argobj)
			{
				if(argobj==null) return false;
				if(argobj.pid==null || argobj.oElem==null || argobj.iDelay==null || argobj.iItem==null)
				{
					return false;
				}
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null)
				{
					return false;
				}
				var jElem = $(argobj.oElem);
				var jItem = jElem.find(".cl-char[data-i='" + argobj.iItem + "']");
				if(jItem.length==0)
				{
					oCurObj.FireEvent({ sName: "onTypeWriterEnd" });
					return false;
				}
				jItem.contents().unwrap();
				oCurObj.iTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: argobj.oElem, iDelay: argobj.iDelay, iItem: (argobj.iItem+1) } }); }, argobj.iDelay);
				return true;
			}

function UIEvent(argobj)
			{
				if(argobj.evt==null) return false;
				var jElem = $(argobj.elem);
				switch(argobj.evt.type)
				{
					case "click":
					{
						CLO[argobj.pid].CallMethod({ sMethod: "Close" });
						break;
					}
				}
				return true;
			}

function Close(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				oCurObj.Hide();
				oCurObj.FireEvent({ sName: "onCloseObject" });
				return true;
			}

function RefreshText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onRefresh" });
				return true;
			}

function ResetText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onReset" });
				return true;
			}

function AddText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				var sText;
				for(var sState in oCurObj.oStates)
				{
					sText = CL.Common.AddText({ sHTML: oCurObj.oStates[sState].sText, sText: argobj.value, sWhere: argobj.where, sHow: argobj.how });
					oCurObj.oStates[sState].sText = sText;
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box-cell").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onAddText" });
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return "";
				if(CLO[argobj.pid]==null) return "";
				switch(argobj.property.toLowerCase())
				{
					case "state":
					{
						return oCurObj.sCurrentState;
					}
				}
				return "";
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" }, { sId: argobj.pid, sEvtName: "EVENT_OBJECT_HIDE" } ] });
				oCurObj.data = { bActive: false, sHLType: oCurObj.jxParams.find("highlight_type").text(), nDuration: +oCurObj.jxParams.find("timer").text() };
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null || oCurObj.div==null) return false;
				if(oCurObj.data.sHLType=="simple") return false;
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(oCurObj.data.bActive) return true;
						oCurObj.CallMethod({ sMethod: "On", oMethodArgs: { bDeferred: true } });
						break;
					}
					case "EVENT_OBJECT_HIDE":
					{
						if(!oCurObj.data.bActive) return true;
						oCurObj.data.bActive = false;
						clearTimeout(oCurObj.iHLTimer);
						break;
					}
				}
				return true;
			}

function On(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null || oCurObj.div==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				if(jCurObjDiv.is(":visible"))
				{
					switch(oCurObj.data.sHLType)
					{
						case "blink":
						{
							jCurObjDiv.find(".cl-notifier").show({ effect: "fade", duration: 100 });
							break;
						}
						case "pulse":
						{
							var iX = Math.floor(0.03*oCurObj.current.w);
							var iY = Math.floor(0.03*oCurObj.current.h);
							jCurObjDiv.find(".cl-notifier").animate({ "left": -iX + "px", "top": -iY + "px", "zoom": 1.06 }, 100);
							break;
						}
					}
					oCurObj.iHLTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "Off" }); }, +oCurObj.data.nDuration);
				}
				return true;
			}

function Off(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null || oCurObj.div==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				if(jCurObjDiv.is(":visible"))
				{
					switch(oCurObj.data.sHLType)
					{
						case "blink":
						{
							jCurObjDiv.find(".cl-notifier").hide({ effect: "fade", duration: 100 });
							break;
						}
						case "pulse":
						{
							jCurObjDiv.find(".cl-notifier").css({ "zoom": 1.0, "left": "0", "top": "0" });
							break;
						}
					}
					oCurObj.iHLTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "On" }); } , +oCurObj.data.nDuration);
				}
				else
				{
					clearTimeout(oCurObj.iHLTimer);
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false;
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				oCurObj.data =
				{
					bRestore: (oCurObj.jxParams.find("restore_state").text()=="yes"),
					bDisplayDefaultText: (oCurObj.jxParams.find("display_default_text").text()=="yes"),
					bDisplayRibbon: (oCurObj.jxParams.find("display_ribbon").text()=="yes"),
					bSpecColorsIdle: (oCurObj.jxParams.find("tab_color_scheme").text()=="spec"),
					bSpecColorsSelected: (oCurObj.jxParams.find("tab_color_scheme_selected").text()=="spec"),
					iTotalElems: oCurObj.jxParams.find("elements > item").length,
					sTabType: (oCurObj.jxParams.children("tab_type").text()),
					sSoundClick: oCurObj.jxParams.children("sound_click").text(),
					sSoundOver: oCurObj.jxParams.children("sound_over").text()
				};
				if(oCurObj.data.sSoundClick=="custom")
				{
					oCurObj.data.sSoundClickFile = oCurObj.jxParams.children("sound_click_file_custom").text();
				}
				if(oCurObj.data.sSoundOver=="custom")
				{
					oCurObj.data.sSoundOverFile = oCurObj.jxParams.children("sound_over_file_custom").text();
				}
				if(CLZ.oStore[argobj.pid]!=null && oCurObj.data.bRestore)
				{
					oCurObj.data.iCurrent = CLZ.oStore[argobj.pid][CLZ.oStore[argobj.pid].length-1];
				}
				else
				{
					CLZ.oStore[argobj.pid] = [];
					var aElementDisplay = oCurObj.jxParams.find("elements > item > display_first");
					var iIdx = -1;
					for(var i=0; i<aElementDisplay.length; i++)
					{
						if($(aElementDisplay[i]).text()=="yes")
						{
							iIdx = i + 1;
							break;
						}
					}
					if(iIdx==-1)
					{
						if(oCurObj.data.bDisplayDefaultText)
						{
							oCurObj.data.iCurrent = 0;
						}
						else
						{
							oCurObj.data.iCurrent = 1;
						}
					}
					else
					{
						oCurObj.data.iCurrent = iIdx;
					}
				}
				if(CL.bTouch)
				{
					jCurObjDiv.find(".cl-tab").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
					if(oCurObj.data.bDisplayRibbon)
					{
						jCurObjDiv.find(".cl-header-btn").on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
					}
				}
				else
				{
					jCurObjDiv.find(".cl-tab").css({ "cursor": "pointer" }).on("mouseover", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
					if(oCurObj.data.bDisplayRibbon)
					{
						jCurObjDiv.find(".cl-header-btn").on("mouseover", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("mouseout", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); }).on("click", function (e) { CLO[argobj.pid].CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: e, elem: this } }); });
					}
				}
				oCurObj.data.bCreated = true;
				oCurObj.FireEvent({ sName: "onCreated" });
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display", oMethodArgs: { elindex: oCurObj.data.iCurrent, bInit: true } });
				}
				return true;
			}

function HandleEvt(argobj)
			{
				if(CLO[argobj.pid]==null || CLO[argobj.pid].div==null) return false;
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Display", oMethodArgs: { elindex: oCurObj.data.iCurrent, bInit: true } });
						break;
					}
				}
				return true;
			}

function UIEvent(argobj)
			{
				if(argobj.evt==null) return false;
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = $(argobj.elem);
				var iTabIdx = jElem.attr("data-number");
				if(jElem.hasClass("cl-tab") && !jElem.hasClass("cl-tab-selected"))
				{
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							if(oCurObj.data.bSpecColorsIdle)
							{
								jElem.removeClass("cl-tab-idle-" + iTabIdx).addClass("cl-tab-over-" + iTabIdx);
							}
							else
							{
								jElem.removeClass("cl-tab-idle").addClass("cl-tab-over");
							}
							CL.Sound.Stop({ sId: argobj.pid, sEvent: "over" });
							if(oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
							}
							break;
						}
						case "mouseout":
						{
							if(oCurObj.data.bSpecColorsIdle)
							{
								jElem.removeClass("cl-tab-over-" + iTabIdx).addClass("cl-tab-idle-" + iTabIdx);
							}
							else
							{
								jElem.removeClass("cl-tab-over").addClass("cl-tab-idle");
							}
							break;
						}
						case "click":
						{
							if(oCurObj.data.bSpecColorsIdle)
							{
								jElem.removeClass("cl-tab-over-" + iTabIdx).addClass("cl-tab-idle-" + iTabIdx);
							}
							else
							{
								jElem.removeClass("cl-tab-over").addClass("cl-tab-idle");
							}
							CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
							if(oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
							}
							oCurObj.CallMethod({ sMethod: "Display", oMethodArgs: { elem: argobj.elem } });
							break;
						}
					}
				}
				else if(jElem.hasClass("cl-header-btn"))
				{
					if(jElem.hasClass("cl-header-btn-disabled")) return false;
					switch(argobj.evt.type)
					{
						case "mouseover":
						{
							jElem.removeClass("cl-header-btn-idle").addClass("cl-header-btn-over");
							if(oCurObj.data.sSoundOver!="none")
							{
								CL.Sound.Play({ sId: argobj.pid, sEvent: "over", sType: oCurObj.data.sSoundOver, sFile: oCurObj.data.sSoundOverFile })
							}
							break;
						}
						case "mouseout":
						{
							jElem.removeClass("cl-header-btn-over").addClass("cl-header-btn-idle");
							break;
						}
						case "click":
						{
							jElem.removeClass("cl-header-btn-over").addClass("cl-header-btn-idle");
							CL.Sound.Stop({ sId: argobj.pid, sEvent: "click" });
							if(oCurObj.data.sSoundClick!="none")
							{
								CL.Sound.Play({ sId: argobj.pid, sEvent: "click", sType: oCurObj.data.sSoundClick, sFile: oCurObj.data.sSoundClickFile })
							}
							oCurObj.CallMethod({ sMethod: (jElem.hasClass("cl-header-btn-left") ? "Prev" : "Next") });
							break;
						}
					}
				}
				return true;
			}

function UpdateButtons(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jPrev = jCurObjDiv.find(".cl-header-btn-left");
				var jNext = jCurObjDiv.find(".cl-header-btn-right");
				jPrev.removeClass("cl-header-btn-disabled");
				jNext.removeClass("cl-header-btn-disabled");
				if(oCurObj.data.iCurrent<=1 && oCurObj.data.iTotalElems<=1)
				{
					jPrev.addClass("cl-header-btn-disabled");
					jNext.addClass("cl-header-btn-disabled");
				}
				else if(oCurObj.data.iCurrent==0 && oCurObj.data.iTotalElems>=1)
				{
					jPrev.addClass("cl-header-btn-disabled");
				}
				else if(oCurObj.data.iCurrent==1 && oCurObj.data.iTotalElems>1)
				{
					oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: { type: "mouseout" }, elem: jPrev } });
					jPrev.addClass("cl-header-btn-disabled");
				}
				else if(oCurObj.data.iCurrent==oCurObj.data.iTotalElems)
				{
					oCurObj.CallMethod({ sMethod: "UIEvent", oMethodArgs: { evt: { type: "mouseout" }, elem: jNext } });
					jNext.addClass("cl-header-btn-disabled");
				}
				return true;
			}

function Next(argobj)
			{
				if(CLO[argobj.pid].div==null) return false;
				CLO[argobj.pid].CallMethod({ sMethod: "Display", oMethodArgs: { elindex: CLO[argobj.pid].data.iCurrent + 1 } });
				return true;
			}

function Prev(argobj)
			{
				if(CLO[argobj.pid].div==null) return false;
				CLO[argobj.pid].CallMethod({ sMethod: "Display", oMethodArgs: { elindex: CLO[argobj.pid].data.iCurrent - 1 } });
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj.div==null) return false;
				var jCurObjDiv = $(oCurObj.div);
				var iIndex = 1;
				if(argobj.elindex!=null)
				{
					iIndex = parseInt(argobj.elindex, 10);
					if(isNaN(iIndex)) return false;
					if(iIndex==0 && oCurObj.data.bDisplayDefaultText)
					{
						oCurObj.CallMethod({ sMethod: "UpdateButtons" });
						oCurObj.data.bDisplayDefaultText =  false;
						return true;
					}
				}
				else
				{
					if(argobj.elem!=null)
					{
						if($(argobj.elem).hasClass("cl-tab"))
						{
							iIndex = +$(argobj.elem).attr("data-number");
						}
						else if($(argobj.elem).hasClass("cl-header-btn-right"))
						{
							iIndex = CLO[argobj.pid].data.iCurrent + 1;
						}
						else if($(argobj.elem).hasClass("cl-header-btn-left"))
						{
							iIndex = CLO[argobj.pid].data.iCurrent - 1;
						}
					}
				}
				if(iIndex<1 || iIndex>oCurObj.data.iTotalElems) return false;
				jCurObjDiv.find(".cl-tab-bg, .cl-desc").hide();
				var iTabIdx = 1;
				jCurObjDiv.find(".cl-tab").each(function ()
				{
					if(oCurObj.data.bSpecColorsSelected)
					{
						$(this).removeClass("cl-tab-selected cl-tab-selected-" + iTabIdx);
					}
					else
					{
						$(this).removeClass("cl-tab-selected");
					}
					if(oCurObj.data.bSpecColorsIdle)
					{
						$(this).addClass("cl-tab-idle-" + iTabIdx);
					}
					else
					{
						$(this).addClass("cl-tab-idle");
					}
					iTabIdx++;
				});
				$("#" + argobj.pid + "_TAB_BG_" + iIndex).show();
				$("#" + argobj.pid + "_DESC_" + iIndex).show();
				var jTab = $("#" + argobj.pid + "_TAB_" + iIndex);
				if(oCurObj.data.bSpecColorsIdle)
				{
					jTab.removeClass("cl-tab-idle-" + iIndex);
				}
				else
				{
					jTab.removeClass("cl-tab-idle");
				}
				if(oCurObj.data.bSpecColorsSelected)
				{
					jTab.addClass("cl-tab-selected cl-tab-selected-" + iIndex);
				}
				else
				{
					jTab.addClass("cl-tab-selected");
				}
				oCurObj.FireEvent({ sName: "onDisplayItem" });
				if(iIndex==1 && oCurObj.data.iTotalElems>1)
				{
					jCurObjDiv.find(".cl-desc-bg").addClass("cl-desc-bg-first").removeClass("cl-desc-bg-last cl-desc-bg-regular");
					oCurObj.FireEvent({ sName: "onDisplayFirstItem" });
				}
				else if(iIndex==oCurObj.data.iTotalElems && oCurObj.data.iTotalElems>1)
				{
					jCurObjDiv.find(".cl-desc-bg").addClass("cl-desc-bg-last").removeClass("cl-desc-bg-first cl-desc-bg-regular");
					oCurObj.FireEvent({ sName: "onDisplayLastItem" });
				}
				else
				{
					jCurObjDiv.find(".cl-desc-bg").removeClass("cl-desc-bg-last cl-desc-bg-first").addClass("cl-desc-bg-regular");
				}
				var iIdx = $.inArray(iIndex, CLZ.oStore[argobj.pid]);
				if(iIdx==-1)
				{
					CLZ.oStore[argobj.pid].push(iIndex);
					if(CLZ.oStore[argobj.pid].length>=oCurObj.data.iTotalElems)
					{
						oCurObj.FireEvent({ sName: "onAllVisited" });
					}
				}
				else
				{
					CLZ.oStore[argobj.pid].splice(iIdx, 1);
					CLZ.oStore[argobj.pid].push(iIndex);
				}
				CL.Sound.Stop({ sId: argobj.pid, sEvent: "narration" });
				var sNarrationFile = $(oCurObj.jxParams.find("elements > item")[iIndex-1]).children("narration").text();
				if(sNarrationFile!="")
				{
					CL.Sound.Play({ sId: argobj.pid + "_" + argobj.iLevel, sEvent: "narration", sType: "custom", sFile: sNarrationFile });
				}
				else
				{
					CL.Sound.Stop({ sId: argobj.pid, sEvent: "narration" });
				}
				oCurObj.data.iCurrent = iIndex;
				oCurObj.CallMethod({ sMethod: "UpdateButtons" });
				return true;
			}

function GetProperty(argobj)
			{
				if(CLO[argobj.pid].div==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "allvisited":
					{
						return ((CLZ.oStore[argobj.pid].length>=oCurObj.data.iTotalElems) ? 1 : 0);
					}
					case "item":
					{
						return CLO[argobj.pid].data.iCurrent;
					}
				}
				return true;
			}

function Constructor(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var bDefer = false; 
				switch(oCurObj.display)
				{
					case "none":
					{
						bDefer = true;
						CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						break;
					}
					case "time":
					case "frame":
					case "slide":
					{
						if(oCurObj.iBegin!=null && +oCurObj.iBegin!=0)
						{
							bDefer = true;
							CL.Event.Subscribe({ aList: [ { sId: argobj.pid, sEvtName: "EVENT_OBJECT_SHOW" } ] });
						}
						break;
					}
				}
				oCurObj.data =
				{
					bTypeWriter: (oCurObj.jxParams.children("typewriter").text()=="yes")
				};
				if(oCurObj.data.bTypeWriter)
				{
					var sSpeed = oCurObj.jxParams.children("typewriter_speed").text();
					var iSpeed = (sSpeed=="") ? 10 : parseInt(sSpeed, 10);
					if(isNaN(iSpeed)) iSpeed = 10;
					oCurObj.data.iDelay = Math.round(1000/iSpeed);
				}
				var sHTML;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					sHTML = CL.Substitute.All({ sString: oCurObj.oStates[sState].sText });
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box").html( sHTML );
				}
				oCurObj.data.bCreated = true;
				if(!bDefer)
				{
					oCurObj.CallMethod({ sMethod: "Display" });
				}
				else
				{
					jCurObjDiv.find(".cl-container:first").hide();
				}
				return true;
			}

function HandleEvt(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				if(oCurObj==null || jCurObjDiv==null) return false; 
				switch(argobj.evt)
				{
					case "EVENT_OBJECT_SHOW":
					{
						if(!oCurObj.data.bCreated) return false;
						oCurObj.CallMethod({ sMethod: "Display" });
						break;
					}
				}
				return true;
			}

function Display(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				jCurObjDiv.find(".cl-container:first").show();
				if(oCurObj.data.bTypeWriter)
				{
					oCurObj.CallMethod({ sMethod: "TypeWriter", oMethodArgs: { oElem: jCurObjDiv.find("div[data-state='default'] .cl-txt-box"), iDelay: oCurObj.data.iDelay, iItem: 0 } });
				}
				return true;
			}

function TypeWriter(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jElem = $(argobj.oElem); 
				if(jElem.attr("data-wrapped")=="yes")
				{
					jElem.find(".cl-char").each(function () { $(this).contents().unwrap() });
				}
				jElem.attr({ "data-wrapped": "yes" }).html( CL.Common.WrapCharacters({ sHTML: jElem.html() }) );
				oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: jElem, iDelay: oCurObj.data.iDelay, iItem: 0 } });
				return true;
			}

function NextChar(argobj)
			{
				if(argobj==null) return false;
				if(argobj.pid==null || argobj.oElem==null || argobj.iDelay==null || argobj.iItem==null)
				{
					return false;
				}
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null)
				{
					return false;
				}
				var jElem = $(argobj.oElem);
				var jItem = jElem.find(".cl-char[data-i='" + argobj.iItem + "']");
				if(jItem.length==0)
				{
					oCurObj.FireEvent({ sName: "onTypeWriterEnd" });
					return false;
				}
				jItem.contents().unwrap();
				oCurObj.iTimer = setTimeout(function () { oCurObj.CallMethod({ sMethod: "NextChar", oMethodArgs: { oElem: argobj.oElem, iDelay: argobj.iDelay, iItem: (argobj.iItem+1) } }); }, argobj.iDelay);
				return true;
			}

function RefreshText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onRefresh" });
				return true;
			}

function ResetText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				if(oCurObj==null) return false; 
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				for(var sState in oCurObj.oStates)
				{
					oCurObj.oStates[sState].sText = (sState=="default") ? oCurObj.jxParams.children("text_main").text() : oCurObj.oStates[sState].jxParams.children("text_main").text();
					jState = jCurObjDiv.find("div[data-state='" + sState + "']");
					jState.find(".cl-txt-box").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onReset" });
				return true;
			}

function AddText(argobj)
			{
				var oCurObj = CLO[argobj.pid];
				var jCurObjDiv = $(oCurObj.div);
				var jState;
				var sText;
				for(var sState in oCurObj.oStates)
				{
					sText = CL.Common.AddText({ sHTML: oCurObj.oStates[sState].sText, sText: argobj.value, sWhere: argobj.where, sHow: argobj.how });
					oCurObj.oStates[sState].sText = sText;
					jCurObjDiv.find("div[data-state='" + sState + "'] .cl-txt-box").html(  CL.Substitute.All({ sString: oCurObj.oStates[sState].sText }) );
				}
				oCurObj.FireEvent({ sName: "onAddText" });
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return "";
				if(CLO[argobj.pid]==null) return "";
				switch(argobj.property.toLowerCase())
				{
					case "state":
					{
						return oCurObj.sCurrentState;
					}
				}
				return "";
			}
